const [factures, setFactures] = useState([]);
const [loading, setLoading] = useState(true);
const [error, setError] = useState(null);

useEffect(() => {
  axios
    .get('http://localhost:7777/factureAchat/')
    .then((res) => {
      console.log('API Response:', res.data); // Log the response data
      setFactures(res.data);
      setLoading(false);
    })
    .catch((err) => {
      console.log('Error fetching data:', err);
      setError('Error fetching data. Please check console for details.');
      setLoading(false);
    });
}, []);

useEffect(() => {
  console.log('Factures State:', factures);
}, [factures]);

if (loading) {
  return <p>Loading...</p>;
}

if (error) {
  return <p>{error}</p>;
}

return (
  <div>
    <h2>Test Fetch Component</h2>
    <p>API Response displayed:</p>
    <table className="table table-striped">
      <thead>
        <tr>
          <th>Id</th>
          <th>Code fss</th>
          <th>Nom du fss</th>
          <th>Articles</th>
          <th>Total</th>
        </tr>
      </thead>
      <tbody>
        {factures.map((facture) => (
          <tr key={facture.id}>
            <td>{facture.id}</td>
            <td>{facture.fssID}</td>
            <td>{facture.fssName}</td>
            <td>
              {Array.isArray(facture.articles) && facture.articles.length > 0 ? (
                facture.articles.map((article, i) => (
                  <span key={i}>
                    {`${article.refAr}: ${article.quantité} | `}
                  </span>
                ))
              ) : (
                <span>No articles</span>
              )}
            </td>
            <td>{facture.totalPrice}</td>
          </tr>
        ))}
      </tbody>
    </table>
  </div>
);
