import axios from 'axios';
import React, { useState } from 'react';
import { useNavigate,useParams } from 'react-router-dom';


function UpdateArticle() {
    const [name,setName] = useState('')
    const [prix1,setPrix1] = useState('')
    const [prix2,setPrix2] = useState('')
    const [tva,setTva] = useState('')
    const {id} = useParams();

    const navigate = useNavigate();


    function handleSubmit(event) {
        event.preventDefault();
        axios.put('http://localhost:7777/article/updateAr/' + id,{ 
            arName: name,
            arPrice1: prix1,
            arPrice2: prix2,
            arTva: tva
    })
        .then(res => {
            console.log(res);
            navigate('/article/');
    
        }).catch(err => console.log(err))
    } 
    
  return (
    <div className='d-flex vh-100 bg-primary justify-content-center align-items-center'>
        <div className='w-50 bg-white rounded p-3'>
            <form onSubmit={handleSubmit}>
                <h2>Modifiez l'article</h2>
                <div className='mb-2'>
                    <label htmlFor="">Nom</label>
                    <input type='text' placeholder="Modifiez le nom d'article" className='form-control'
                    onChange={e => setName(e.target.value)}
                    ></input>
                </div>
                <div className='mb-2'>
                    <label htmlFor="">1er Prix</label>
                    <input type='text' placeholder="Modifiez le premier prix de cet article" className='form-control'
                    onChange={e => setPrix1(e.target.value)}
                    ></input>
                </div>
                <div className='mb-2'>
                    <label htmlFor="">2ème Prix</label>
                    <input type='text' placeholder="Modifiez le deuxième prix de cet article" className='form-control'
                    onChange={e => setPrix2(e.target.value)}
                    ></input>
                </div>
                <div className='mb-2'>
                    <label htmlFor="">TVA</label>
                    <input type='text' placeholder='Modifiez le TVA de cet article' className='form-control'
                    onChange={e => setTva(e.target.value)}
                    ></input>
                </div>
                <button className='btn btn-success'>Ajouter</button>
            </form>
        </div>
    </div>
  )
}

export default UpdateArticle;
