import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function CreateArticle() {
  const [name, setName] = useState("");
  const [ref, setRef] = useState("");
  const [achatbrut, setAchatbrut] = useState("");
  const [remise1, setRemise1] = useState("");
  const [remise2, setRemise2] = useState("");
  const [achatttc, setAchatttc] = useState("");
  const [achatc, setAchatc] = useState("");
  const [mbenif, setMbenif] = useState("");
  const [venteht, setVenteht] = useState("");
  const [ventettc, setVentettc] = useState("");
  const [autofillar1, setAutofillar1] = useState("");
  const [autofillar2, setAutofillar2] = useState("");
  const [selectedTva, setSelectedTva] = useState("0"); 

  const navigate = useNavigate();

  const handleSubmit = (event) => {
    event.preventDefault();
    if (selectedTva !== null && selectedTva !== undefined) {
      axios
        .post("http://localhost:7777/article/createAr/", {
          arName: name,
          ref: ref,
          prachat: achatbrut,
          remise1: remise1,
          prar1: autofillar1,
          remise2: remise2,
          prar2: autofillar2,
          tva: parseInt(selectedTva),
          achatttc: achatttc,
          achatc: achatc,
          margeben: mbenif,
          prventeht: venteht,
          prventettc: ventettc,
        })
        .then((res) => {
          console.log(selectedTva);
          navigate("/article");
        })
        .catch((err) => console.log(err));
    } else {
      console.log("Invalid value of tva");
    }
  }

  useEffect(() => {
    // Calculate Prix d'achat AR1
    if (achatbrut && remise1) {
      const prixAR1 = parseFloat(achatbrut) * (1 - parseFloat(remise1) / 100);
      setAutofillar1(prixAR1.toFixed(2));
    } else {
      setAutofillar1('');
    }
  }, [achatbrut, remise1]);

  useEffect(() => {
    // Calculate Prix d'achat AR2
    if (autofillar1 && remise2) {
      const prixAR2 = parseFloat(autofillar1) * (1 - parseFloat(remise2) / 100);
      setAutofillar2(prixAR2.toFixed(2));
    } else {
      setAutofillar2('');
    }
  }, [autofillar1, remise2]);

  useEffect(() => {
    if (autofillar1 && remise2) {
      // If autofillar1 and remise2 have values, set achatttc to autofillar2
      setAchatttc(autofillar2);
    } else {
      // Otherwise, set achatttc to autofillar1
      setAchatttc(autofillar1);
    }
  }, [autofillar1, autofillar2, remise2]);
  
  useEffect(() => {
    // Check if both achatttc and mbenif have values
    if (achatttc && mbenif) {
      // Calculate the sum of achatttc and mbenif
      const sum = (parseFloat(achatttc) / 100 * parseFloat(mbenif)) + parseFloat(achatttc);
      // Set the sum as the value of venteht
      setVenteht(sum.toString());
    } else {
      // Clear venteht if any of the inputs are empty
      setVenteht('');
    }
  }, [achatttc, mbenif]);

  useEffect(() => {
    if (venteht && selectedTva) {
      const sum = parseFloat(venteht) + (parseFloat(venteht) * parseFloat(selectedTva) / 100);
      setVentettc(sum.toFixed(2));
    } else {
      setVentettc('');
    }
  }, [venteht, selectedTva]);

  useEffect(() => {
    if (achatttc && selectedTva) {
      const sum = parseFloat(achatttc) + (parseFloat(achatttc) * parseFloat(selectedTva) / 100);
      setAchatc(sum.toFixed(2));
    } else {
      setAchatc('');
    }
  }, [achatttc, selectedTva]);
  
  return (
    <div className="container-fluid">
      <div className="row justify-content-center">
        <div className="col-lg-8 col-md-10 col-sm-12">
          <h2>Ajouter un article</h2>
          <form onSubmit={handleSubmit}>
            {/* Your form content here */}
            <form onSubmit={handleSubmit}>
        <div className="row mb-3">
          <div className="col">
            <label htmlFor="name" className="form-label">Nom d'article</label>
            <input
              type="text"
              id="name"
              className="form-control"
              placeholder="Entrez le nom d'article"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div className="col">
            <label htmlFor="ref" className="form-label">Référence</label>
            <input
              type="text"
              id="ref"
              className="form-control"
              placeholder="Entrez la référence de cet article"
              value={ref}
              onChange={(e) => setRef(e.target.value)}
            />
          </div>
        </div>
        <div className="row mb-3">
          <div className="col">
            <label htmlFor="achatbrut" className="form-label">Prix d'achat brut</label>
            <input
              type="number"
              id="achatbrut"
              className="form-control"
              placeholder="Entrez le prix d'achat brut de cet article"
              value={achatbrut}
              onChange={(e) => setAchatbrut(e.target.value)}
            />
          </div>
          <div className="col">
            <label htmlFor="remise1" className="form-label">Remise 1</label>
            <input
              type="number"
              id="remise1"
              className="form-control"
              placeholder="Entrez la 1ère remise de cet article"
              value={remise1}
              onChange={(e) => setRemise1(e.target.value)}
            />
          </div>
        </div>
        <div className="row mb-3">
          <div className="col">
            <label htmlFor="autofillar1" className="form-label">Prix d'achat AR1</label>
            <input
              type="text"
              id="autofillar1"
              className="form-control"
              readOnly
              value={autofillar1}
            />
          </div>
          <div className="col">
            <label htmlFor="remise2" className="form-label">Remise 2</label>
            <input
              type="text"
              id="remise2"
              className="form-control"
              placeholder="Entrez la 2ème remise de cet article"
              value={remise2}
              onChange={(e) => setRemise2(e.target.value)}
            />
          </div>
        </div>
        <div className="row mb-3">
          <div className="col">
            <label htmlFor="autofillar2" className="form-label">Prix d'achat AR2</label>
            <input
              type="text"
              id="autofillar2"
              className="form-control"
              readOnly
              value={autofillar2}
            />
          </div>
          <div className="col">
            <label htmlFor="achatttc" className="form-label">Prix d'achat HT</label>
            <input
              type="text"
              id="achatttc"
              className="form-control"
              placeholder="Entrez le HT de cet article"
              value={achatttc}
              onChange={(e) => setAchatttc(e.target.value)}
            />
          </div>
        </div>
        <div className="row mb-3">
          <div className="col">
            <label htmlFor="tva" className="form-label">TVA</label><br />
            <select
              id="tva"
              className="form-select"
              value={selectedTva}
              onChange={(e) => setSelectedTva(e.target.value)}
            >
              <option value="0">0%</option>
              <option value="7">7%</option>
              <option value="13">13%</option>
              <option value="19">19%</option>
            </select>
          </div>
          <div className="col">
            <label htmlFor="achatc" className="form-label">Prix d'achatc TTC</label>
            <input
              type="text"
              id="achatc"
              className="form-control"
              readOnly
              value={achatc}
            />
          </div>
        </div>
        <div className="row mb-3">
          <div className="col">
            <label htmlFor="mbenif" className="form-label">Marge bénéficiaire</label>
            <input
              type="text"
              id="mbenif"
              className="form-control"
              placeholder="Entrez la marge bénéficiaire désirée"
              value={mbenif}
              onChange={(e) => setMbenif(e.target.value)}
            />
          </div>
          <div className="col">
            <label htmlFor="venteht" className="form-label">Prix de vente HT</label>
            <input
              type="text"
              id="venteht"
              className="form-control"
              readOnly
              value={venteht}
            />
          </div>
        </div>
        <div className="row mb-3">
          <div className="col">
            <label htmlFor="ventettc" className="form-label">Prix de vente TTC</label>
            <input
              type="text"
              id="ventettc"
              className="form-control"
              readOnly
              value={ventettc}
            />
          </div>
        </div>
        <button type="submit" className="btn btn-success">Ajouter</button>
      </form>
          </form>
        </div>
      </div>
    </div>
  );
}

export default CreateArticle;
