import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Modal from 'react-modal';
import CreateFactureAchat from './CreateFactureVente';
import { FaSearch } from 'react-icons/fa';

Modal.setAppElement('#root');

function FactureAchat() {
  const [fAchat, setFAchat] = useState([]);
  const [selectedFacture, setSelectedFacture] = useState(null);
  const [selectedRow, setSelectedRow] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [loading, setLoading] = useState(true); // Added loading state

  useEffect(() => {
    axios
      .get('http://localhost:7777/factureAchat/')
      .then((res) => {
        console.log('API Response:', res.data); // Log the response data
        const formattedFAchat = res.data.map((facture) => {
          const date = new Date(facture.date);
          const formattedDate = date.toISOString().split('T')[0]; // Ensure date is in YYYY-MM-DD format
          return {
            ...facture,
            date: formattedDate,
            articles: Array.isArray(facture.articles) ? facture.articles : [], // Ensure articles is always an array
          };
        });
        setFAchat(formattedFAchat);
        console.log('Formatted Data:', formattedFAchat); // Log the formatted data
      })
      .catch((err) => console.log('Error fetching data:', err));
  }, []);
  

  // const filteredFAchat = fAchat.filter((facture) => {
  //   return (
  //     facture.clName?.toLowerCase()?.includes(searchQuery.toLowerCase()) ||
  //     (Array.isArray(facture.articles) && 
  //      facture.articles.some(article => 
  //        article.refAr.toLowerCase().includes(searchQuery.toLowerCase())
  //      )
  //     )
  //   );
  // });
  const filteredFAchat = fAchat.filter((facture) => {
    return true; // Temporarily return true to see all items
  });
  
  
  

  const handleSearch = (query) => {
    setSearchQuery(query);
  };

  const handleSubmit = (newFactureData) => {
    axios.post('http://localhost:7777/factureAchat/', newFactureData)
      .then(res => {
        setModalIsOpen(false);
        setFAchat([...fAchat, res.data]);
      })
      .catch(err => console.log('Error creating facture:', err));
  };
  useEffect(() => {
    console.log('fAchat State:', fAchat); // Log fAchat state
  }, [fAchat]);
  
  useEffect(() => {
    console.log('Search Query:', searchQuery); // Log search query
  }, [searchQuery]);
  
  useEffect(() => {
    console.log('Filtered Data:', filteredFAchat); // Log filtered data
  }, [searchQuery, fAchat]);
  

  return (
    <div className="facture-vente-container">
      <CreateFactureAchat onFactureSubmit={handleSubmit} selectedFacture={selectedFacture} />
      <button onClick={() => setModalIsOpen(true)}>Voir les factures de vente</button>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={() => setModalIsOpen(false)}
        contentLabel="Voir les factures de vente"
        style={{
          content: {
            top: '50%',
            left: '50%',
            right: 'auto',
            bottom: 'auto',
            marginRight: '-50%',
            transform: 'translate(-50%, -50%)',
            width: '80%',
            maxWidth: '800px',
            maxHeight: '80vh',
            overflowY: 'auto',
          },
        }}
      >
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h2 className="mb-4">Factures d'achat :</h2>
          <div className="input-group">
            <input
              type="text"
              className="form-control"
              placeholder="Rechercher un client ou une référence..."
              value={searchQuery}
              onChange={(e) => handleSearch(e.target.value)}
            />
            <span className="input-group-text">
              <FaSearch />
            </span>
          </div>
        </div>
        <div className="table-responsive">
          {loading ? (
            <p>Loading...</p>
          ) : (
            <table className="table table-striped">
              <thead>
                <tr>
                  <th>Id</th>
                  <th>Code fss</th>
                  <th>Nom du fss</th>
                  <th>Articles</th>
                  <th>Total</th>
                </tr>
              </thead>
              {/* <tbody>
                {filteredFAchat.length > 0 ? (
                  filteredFAchat.map((facture, index) => (
                    <tr
                      key={facture.id}
                      onClick={() => {
                        setSelectedFacture(facture);
                        setSelectedRow(index);
                      }}
                      className={selectedRow === index ? 'table-active' : ''}
                      style={{ cursor: 'pointer' }}
                    >
                      <td>{facture.id}</td>
                      <td>{facture.fssID}</td>
                      <td>{facture.fssName}</td>
                      <td>
                        {facture.articles.length > 0 ? (
                          facture.articles.map((article, i) => (
                            <span key={i}>
                              {`${article.refAr}: ${article.quantité} | `}
                            </span>
                          ))
                        ) : (
                          <span>No articles</span>
                        )}
                      </td>
                      <td>{facture.totalPrice}</td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="5" className="text-center">Aucune facture trouvée</td>
                  </tr>
                )}
              </tbody> */}
       <tbody>
  {filteredFAchat.length > 0 ? (
    filteredFAchat.map((facture, index) => (
      <tr
        key={facture.id}
        onClick={() => {
          console.log('Selected Facture:', facture); // Debug log
          setSelectedFacture(facture);
          setSelectedRow(index);
        }}
        className={selectedRow === index ? 'table-active' : ''}
        style={{ cursor: 'pointer' }}
      >
        <td>{facture.id}</td>
        <td>{facture.fssID}</td>
        <td>{facture.fssName}</td>
        <td>
          {facture.articles.map((article, i) => (
            <span key={i}>
              {`${article.refAr}: ${article.quantité} | `}
            </span>
          ))}
        </td>
        <td>{facture.totalPrice}</td>
      </tr>
    ))
  ) : (
    <tr>
      <td colSpan="5" className="text-center">Aucune facture trouvée</td>
    </tr>
  )}
</tbody>



            </table>
          )}
        </div>
      </Modal>
    </div>
  );
}

export default FactureAchat;
