import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

function CreateFactureVente({ selectedFacture }) {
    const [clName, setClName] = useState('');
    const [clientCode, setClientCode] = useState('');
    const [date, setDate] = useState('');
    const [DT, setDT] = useState('1.000');
    const [inputs, setInputs] = useState([{ refAr: '', quantité: '', price: 0, remise: 0 }]);
    const [references, setReferences] = useState([]);
    const [filteredClients, setFilteredClients] = useState([]);
    const [modalIsOpen, setModalIsOpen] = useState(false);

    const remiseInputRef = useRef(null); 

    useEffect(() => {
        axios.get('http://localhost:7777/article')
            .then(res => {
                setReferences(res.data.map(article => article.ref));
            })
            .catch(err => console.log('Error fetching articles:', err));
    }, []);

    useEffect(() => {
        if (selectedFacture) {
            setClName(selectedFacture.clName || '');
            setClientCode(selectedFacture.clientCode || '');
            setDate(selectedFacture.date || '');
            setDT(selectedFacture.DT || '1.000');
            setInputs(selectedFacture.inputs || [{ refAr: '', quantité: '', price: 0, remise: 0 }]);
            setModalIsOpen(true);
        }
    }, [selectedFacture]);

    const fetchPriceByReference = (reference, index) => {
        axios.get(`http://localhost:7777/factureVente/${reference}`)
            .then(res => {
                const price = res.data.price;
                const newInputs = [...inputs];
                newInputs[index].price = price;
                setInputs(newInputs);
            })
            .catch(err => console.log('Error fetching price:', err));
    };

    const handleReferenceChange = (event, index) => {
        const { value } = event.target;
        const newInputs = [...inputs];
        newInputs[index].refAr = value;
        setInputs(newInputs);
        fetchPriceByReference(value, index);
    };

    const handleRemiseChange = (event, index) => {
        const { value } = event.target;
        const newInputs = [...inputs];
        newInputs[index].remise = value;
        setInputs(newInputs);
    };

    const handleQuantityChange = (event, index) => {
        const { value } = event.target;
        const newInputs = [...inputs];
        newInputs[index].quantité = value;

        axios.get(`http://localhost:7777/article/${newInputs[index].refAr}/quantity`)
            .then(res => {
                const availableQuantity = res.data.quantity;
                const remainingQuantity = availableQuantity - parseInt(value);

                if (remainingQuantity < 0) {
                    alert(`La quantité souhaitée n'est pas suffisante, vous n'avez que ${availableQuantity} pièces de cet article`);
                    newInputs[index].quantité = availableQuantity;
                }

                const remisePrice = newInputs[index].price * (1 - (newInputs[index].remise / 100));
                newInputs[index].price = parseFloat(remisePrice.toFixed(2));
                setInputs(newInputs);
            })
            .catch(err => console.error('Error fetching quantity:', err));
            
        const newTotal = newInputs.reduce((acc, curr) => acc + (curr.price * curr.quantité), 0) + parseFloat(DT);
        console.log("New Total:", newTotal);
    };

    const handleAddInput = () => {
        const isEmpty = inputs.some(input => input.refAr === '' || input.quantité === '' || input.remise === '');
        if (isEmpty) {
            alert("Veuillez remplir tous les champs de la ligne précédente avant d'ajouter une nouvelle ligne.");
            return;
        }
        setInputs([...inputs, { refAr: '', price: 0, quantité: '', remise: 0 }]);
    };

    const calculateTotal = () => {
        if (!inputs) return 0; 
        const total = inputs.reduce((acc, curr) => acc + ((curr.price * curr.quantité) / 100) * (100 - curr.remise), 0) + parseFloat(DT); // Add DT to the total
        return total.toFixed(3); // Round to three decimal places
    };

    const handleSubmit = (event) => {
        event.preventDefault();

        // Filter out empty inputs before checking for invalid references
        const nonEmptyInputs = inputs.filter(input => input.refAr !== '' && input.quantité !== '' && input.remise !== '');
        const invalidReference = nonEmptyInputs.some(input => !references.includes(input.refAr));

        if (invalidReference) {
            alert("Sélectionner une référence valide");
            return;
        }

        const refArArray = [];
        const quantiteArray = [];
        const remiseArray = [];
        nonEmptyInputs.forEach(input => {
            refArArray.push(input.refAr);
            quantiteArray.push(input.quantité);
            remiseArray.push(input.remise);
        });

        const totalPrice = calculateTotal();
        axios.post('http://localhost:7777/factureVente/createFac/', {
            clName,
            client_code,
            date,
            DT,
            refAr: refArArray,
            quantité: quantiteArray,
            remise: remiseArray,
            totalPrice
        })
            .then(res => {
                console.log(res);
                window.location.reload();
            })
            .catch(err => console.log('Error creating facture:', err))
    };

    const handleKeyPress = (event) => {
        if (event.key === 'Enter') {
            handleAddInput();
        }
    };

    const handleClientChange = (e) => {
        const { value } = e.target;
        setClName(value);

        // Fetch filtered clients from the server
        axios.get(`http://localhost:7777/clients?q=${value}`)
            .then(res => {
                setFilteredClients(res.data);
            })
            .catch(err => console.log('Error fetching clients:', err));
    };

    const handleClientBlur = () => {
        // Clear the filtered clients list when the input field loses focus
        setFilteredClients([]);
    };

    const handleClientSelect = (name, code) => {
        setClName(name);
        setClientCode(code);
        setFilteredClients([]);
    };

    return (
        <div style={{ margin: '20px', justifyContent: 'center' }}>
            <div style={{ display: 'flex', flex: '1' }}>
                <div>
                    <div style={{ marginBottom: '20px' }}>
                        <label style={{ width: '120px' }} htmlFor="code-client">Code client:</label>
                        <input
                            style={{ width: '200px' }}
                            type="text"
                            id="code-client"
                            name="code-client"
                            value={clientCode} // Display the client code
                            onChange={(e) => setClientCode(e.target.value)} // Allow typing in the client code

                            />
    </div>
                        <div style={{ marginBottom: '20px', position: 'relative' }}>
                            <label style={{ width: '120px' }} htmlFor="client">Nom du client</label>
                            <input
                                style={{ width: '200px' }}
                                type="text"
                                id="client"
                                value={clName}
                                onChange={handleClientChange}
                                onBlur={handleClientBlur} // Add onBlur event to clear filtered clients list
                            />
                            {filteredClients.length > 0 && (
                                <div style={{ position: 'absolute', top: '100%', left: 0, backgroundColor: 'white', border: '1px solid #ccc', width: '100%' }}>
                                    {filteredClients.map((client, index) => (
                                        <div key={index} style={{ padding: '5px', cursor: 'pointer' }} onClick={() => handleClientSelect(client.clName, client.clID)}>
                                            {client.clName}
                                        </div>
                                    ))}
                                </div>
                            )}
                        </div>
                        <div style={{ marginBottom: '20px' }}>
                            <label style={{ width: '120px' }} htmlFor="date">Date</label>
                            <input
                                style={{ width: '200px' }}
                                type="date"
                                id="date"
                                value={date}
                                onChange={(e) => setDate(e.target.value)}
                            />
                        </div>
                        <div style={{ marginBottom: '20px' }}>
                            <label style={{ width: '120px' }} htmlFor="DT">Droit de timbre</label>
                            <input
                                style={{ width: '200px' }}
                                type="text"
                                id="code"
                                value={DT}
                                onChange={(e) => setDT(e.target.value)}
                                onBlur={handleQuantityChange} // Add onBlur event to trigger recalculation when "Droit de timbre" value changes
                            />
                        </div>
                    </div>
                    <div>
                        <div>
                            {inputs.map((input, index) => (
                                <div key={index} style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
                                    <div style={{ flex: '1', marginRight: '10px' }}>
                                        <label>Référence d'article</label>
                                        <input
                                            list="references"
                                            style={{ width: '100%' }}
                                            value={input.refAr}
                                            onChange={(e) => handleReferenceChange(e, index)}
                                        />
                                        <datalist id="references">
                                            {references.map((reference, idx) => (
                                                <option key={idx} value={reference} />
                                            ))}
                                        </datalist>
                                    </div>
                                    <div style={{ flex: '1', marginRight: '10px' }}>
                                        <label>Prix vente TTC</label>
                                        <input
                                            type="number"
                                            style={{ width: '100%' }}
                                            value={input.price}
                                            readOnly
                                        />
                                    </div>
                                    <div style={{ flex: '1', marginRight: '10px' }}>
                                        <label>Quantité</label>
                                        <input
                                            type="number"
                                            style={{ width: '100%' }}
                                            value={input.quantité}
                                            onChange={(e) => handleQuantityChange(e, index)}
                                        />
                                    </div>
                                    <div style={{ display: 'flex', alignItems: 'center' }}>
                                        <div style={{ flex: '1' }}>
                                            <label>Remise</label>
                                            <input
                                                type="number"
                                                style={{ width: '100%' }}
                                                value={input.remise}
                                                onChange={(e) => handleRemiseChange(e, index)}
                                                onKeyDown={handleKeyPress} // Call handleKeyPress on key press
                                                ref={remiseInputRef} // Set the ref for Remise input field
                                            />
                                        </div>
                                        <div style={{ marginLeft: '10px' }}>
                                            <button type="button" onClick={handleAddInput}>+</button>
                                        </div>
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                    <div>
                        <div>
                            <div style={{ display: "inline-block", marginBottom: '20px', textAlign: 'center' }}>
                                <button
                                    style={{ width: '200px', marginBottom: '10px' }}
                                    type="button"
                                    onClick={handleAddInput}
                                >
                                    Ajouter
                                </button>
                                <button
                                    style={{ width: '200px', marginBottom: '10px' }}
                                    type="submit"
                                    onClick={handleSubmit}
                                >
                                    Enregistrer
                                </button>
                            </div>
                        </div>
                    </div>
                </div>
                <div style={{ marginBottom: '20px' }}>
                    <label>Total</label>
                    <input
                        type="text" 
                        style={{ width: '100%' }}
                        value={calculateTotal()}
                        readOnly
                    />
                </div>
            </div>
        );
    }
    
    export default CreateFactureVente;
