import axios from 'axios';
import React, { useState, useEffect } from 'react';

function CreateClient({ onAddClient, onDelete, selectedClient }) {
  const [clientId, setClientId] = useState(''); // New state for client ID search
  const [name, setName] = useState('');
  const [adresse, setAdresse] = useState('');
  const [tel, setTel] = useState('');
  const [MF, setMF] = useState('');
  const [RC, setRC] = useState('');
  const [responsable, setResponsable] = useState('');
  const [resTel, setResTel] = useState('');

  useEffect(() => {
    if (selectedClient) {
      setName(selectedClient.clName || '');
      setAdresse(selectedClient.clAdr || '');
      setTel(selectedClient.clTel || '');
      setMF(selectedClient.clMF || '');
      setRC(selectedClient.clRC || '');
      setResponsable(selectedClient.responsable || '');
      setResTel(selectedClient.resTel || '');
    }
  }, [selectedClient]);

  const handleNameChange = async (event) => {
    const inputValue = event.target.value;
    setName(inputValue);

    if (inputValue) {
      try {
        const response = await axios.get(`http://localhost:7777/client/search?name=${inputValue}`);
        if (response.data) {
          const client = response.data;
          setAdresse(client.clAdr || '');
          setTel(client.clTel || '');
          setMF(client.clMF || '');
          setRC(client.clRC || '');
          setResponsable(client.responsable || '');
          setResTel(client.resTel || '');
        } else {
          clearInputs();
          setName(inputValue); // Retain the input value
        }
      } catch (err) {
        console.log(err);
        clearInputs();
        setName(inputValue); // Retain the input value
      }
    } else {
      clearInputs();
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    // Perform actions based on the client ID entered
  };

  const clearInputs = () => {
    setName('');
    setAdresse('');
    setTel('');
    setMF('');
    setRC('');
    setResponsable('');
    setResTel('');
  };

  return (
    <div style={{ margin: '20px', display: 'flex', justifyContent: 'flex-end' }}>
      <div style={{ flex: '0.7', marginRight: '10px' }}>
        <div style={{ marginBottom: '10px' }}>
          <label style={{ width: '120px' }} htmlFor="clientId">ID Client</label> {/* New input field for client ID */}
          <input
            style={{ width: '200px' }}
            type="text"
            id="clientId"
            value={clientId}
            onChange={(e) => setClientId(e.target.value)}
          />
        </div>
        <div style={{ marginBottom: '10px' }}>
          <label style={{ width: '120px' }} htmlFor="name">Nom</label>
          <input
            style={{ width: '200px' }}
            type="text"
            id="name"
            value={name}
            onChange={handleNameChange}
          />
        </div>
        {/* Remaining input fields */}
      </div>
      {/* Buttons and other elements */}
    </div>
  );
}

export default CreateClient;
