import axios from 'axios';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';


function CreateFactureVente() {
    const [client,setClient] = useState('')
    const [date,setDate] = useState('')
    const [code,setCode] = useState('')
    const [refArticle,setRefArticle] = useState('')
    const [quantité,setQuantité] = useState('')

    const navigate = useNavigate();
// get request for fss names 

function handleSubmit(event) {
    event.preventDefault();
    axios.post('http://localhost:7777/factureVente/createFac/',{
    client: client,
    date: date,
    code: code,
    refArticle: refArticle,
    quantité : quantité
})
    .then(res => {
        console.log(res);
        window.location.reload();
    }).catch(err => console.log(err))
} 
    
  return (
    <div className='container mt-5'>
    <div className='w-50 bg-white rounded p-3'>
        <form onSubmit={handleSubmit}>
            
            <div className='mb-2'>
                <label htmlFor="">Nom du client</label>
                <input type='text' placeholder='entrez le nom du client' className='form-control'
                onChange={e => setClient(e.target.value)}
                ></input>
            </div>
            <div className='mb-2'>
                <label htmlFor="">date</label>
                <input type="date" 
                className='form-control'
                onChange={e => setDate(e.target.value)}
                ></input>
            </div>
            <div className='mb-2'>
                <label htmlFor="">Code de la facture</label>
                <input type='text' placeholder='entrez le code de la facture' className='form-control'
                onChange={e => setCode(e.target.value)}
                ></input>
            </div>
            <div className='mb-2'>
                <label htmlFor="">Réference d'article</label>
                <input type='text' placeholder="entrez la Réference d'article" className='form-control'
                onChange={e => setRefArticle(e.target.value)}
                ></input>
            </div>
            <div className='mb-2'>
                <label htmlFor="">Quantité</label>
                <input type='number' placeholder='entrez la quantité désirée' className='form-control'
                onChange={e => setQuantité(e.target.value)}
                ></input>
            </div>
            <button className='btn btn-success'>Ajouter</button>
        </form>
    </div>
</div>
  )
}

export default CreateFactureVente;
