import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import CreateArticle from './CreateArticle';
import { FaPlus } from 'react-icons/fa';

function Article() {
    const [articles, setArticles] = useState([]);
    const [showSelect, setShowSelect] = useState(false);

    useEffect(() => {
        axios.get('http://localhost:7777/article/')
            .then(res => setArticles(res.data))
            .catch(err => console.log(err));
    }, []);

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:7777/article/${id}`);
            setArticles(articles.filter(article => article.arID !== id));
        } catch (err) {
            console.log(err);
        }
    }

    const handleAddArticle = (newArticle) => {
        setArticles([...articles, newArticle]);
    }

    const toggleSelect = () => {
        setShowSelect(!showSelect);
    }

    return (
        <div className='article-container'>
            <CreateArticle onAddArticle={handleAddArticle} /><br />
            <h2>Liste des Articles</h2>
            <div className='table-responsive'>
                <table className='table table-striped'>
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>Nom</th>
                            <th>Référence</th>
                            <th>Remise1</th>
                            <th>Prix TTC</th>
                            <th>Vente TTC</th>
                            <th>
                                <button className="btn btn-secondary" onClick={toggleSelect}>
                                    <FaPlus />
                                </button>
                                {showSelect && (
                                    <select className="form-select mt-2">
                                        <option>Achat brut</option>
                                        <option>Prix d'achat AR1</option>
                                        <option>Remise2</option>
                                        <option>Achat AR2 HT</option>
                                        <option>TVA</option>
                                        <option>Marge bénéficiaire</option>
                                        <option>Vente HT</option>
                                    </select>
                                )}
                            </th>
                        </tr>
                    </thead>
                    <tbody>
                        {articles.map(article => (
                            <tr key={article.arID}>
                                <td>{article.arID}</td>
                                <td>{article.arName}</td>
                                <td>{article.ref}</td>
                                <td>{article.remise1}</td>
                                <td>{article.achatc}</td>
                                <td>{article.prventettc}</td>
                                <td>
                                    {/* Optional: Add actions for edit and delete here if needed */}
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default Article;













