import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function CreateArticle() {
  const handleRefresh = () => {
    // Reload the page
    window.location.reload();
};

  const [name, setName] = useState("");
  const [ref, setRef] = useState("");
  const [achatbrut, setAchatbrut] = useState("");
  const [remise1, setRemise1] = useState("");
  const [remise2, setRemise2] = useState("");
  const [achatttc, setAchatttc] = useState("");
  const [achatc, setAchatc] = useState("");
  const [mbenif, setMbenif] = useState("");
  const [venteht, setVenteht] = useState("");
  const [ventettc, setVentettc] = useState("");
  const [autofillar1, setAutofillar1] = useState('');
  const [autofillar2, setAutofillar2] = useState('');
  const [selectedTva, setSelectedTva] = useState("0"); 

  const navigate = useNavigate();

  const handleSubmit = (event) => {
    event.preventDefault();
    if (selectedTva !== null && selectedTva !== undefined) {
      axios
        .post("http://localhost:7777/article/createAr/", {
          arName: name,
          ref: ref,
          prachat: achatbrut,
          remise1: remise1,
          prar1: autofillar1,
          remise2: remise2,
          prar2: autofillar2,
          tva: parseInt(selectedTva),
          achatttc: achatttc,
          achatc: achatc,
          margeben: mbenif,
          prventeht: venteht,
          prventettc: ventettc,
        })
        .then((res) => {
          console.log(selectedTva);
          navigate("/article");
        })
        .catch((err) => console.log(err));
    } else {
      console.log("Invalid value of tva");
    }
  }

  useEffect(() => {
    // Calculate Prix d'achat AR1
    if (achatbrut && remise1) {
      const prixAR1 = parseFloat(achatbrut) * (1 - parseFloat(remise1) / 100);
      setAutofillar1(prixAR1.toFixed(2));
    } else {
      setAutofillar1('');
    }
  }, [achatbrut, remise1]);

  useEffect(() => {
    // Calculate Prix d'achat AR2
    if (autofillar1 && remise2) {
      const prixAR2 = parseFloat(autofillar1) * (1 - parseFloat(remise2) / 100);
      setAutofillar2(prixAR2.toFixed(2));
    } else {
      setAutofillar2('');
    }
  }, [autofillar1, remise2]);

  useEffect(() => {
    if (autofillar1 && remise2) {
      // If autofillar1 and remise2 have values, set achatttc to autofillar2
      setAchatttc(autofillar2);
    } else {
      // Otherwise, set achatttc to autofillar1
      setAchatttc(autofillar1);
    }
  }, [autofillar1, autofillar2, remise2]);
  
  useEffect(() => {
    // Check if both achatttc and mbenif have values
    if (achatttc && mbenif) {
      // Calculate the sum of achatttc and mbenif
      const sum = (parseFloat(achatttc) / 100 * parseFloat(mbenif)) + parseFloat(achatttc);
      // Set the sum as the value of venteht
      setVenteht(sum.toString());
    } else {
      // Clear venteht if any of the inputs are empty
      setVenteht('');
    }
  }, [achatttc, mbenif]);

  useEffect(() => {
    if (venteht && selectedTva) {
      const sum = parseFloat(venteht) + (parseFloat(venteht) * parseFloat(selectedTva) / 100);
      setVentettc(sum.toFixed(2));
    } else {
      setVentettc('');
    }
  }, [venteht, selectedTva]);

  useEffect(() => {
    if (achatttc && selectedTva) {
      const sum = parseFloat(achatttc) + (parseFloat(achatttc) * parseFloat(selectedTva) / 100);
      setAchatc(sum.toFixed(2));
    } else {
      setAchatc('');
    }
  }, [achatttc, selectedTva]);
  
  return (
    <div className="d-flex justify-content-center align-items-center">
      <div className="w-50 bg-white rounded p-3">
        <form onSubmit={handleSubmit}>
          <h2>Ajouter un article</h2>
          <div style={{ display: 'flex' }}>
            
          <div className="mb-2">
            <label htmlFor="">Nom d'article</label>
            <input
              type="text"
              placeholder="entrez le nom d'article"
              className="form-control"
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div className="mb-2">
            <label htmlFor="">Référence</label>
            <input
              type="text"
              placeholder="entrez la référence de cet article"
              className="form-control"
              onChange={(e) => setRef(e.target.value)}
            />
          </div>
          <div className="mb-2">
            <label htmlFor="">Prix d'achat brut</label>
            <input
              type="number"
              value={achatbrut}
              placeholder="entrez le prix d'achat brut de cet article"
              className="form-control"
              onChange={(e) => setAchatbrut(e.target.value)}
            />
          </div>
          <div className="mb-2">
            <label htmlFor="">Remise 1</label>
            <input
              type="number"
              value={remise1}
              placeholder="entrez la 1ère remise de cet article"
              className="form-control"
              onChange={(e) => setRemise1(e.target.value)}
            />
          </div>
          <div className="mb-2">
            <label htmlFor="">Prix d'achat AR1</label>
            <input
              type="text"
              value={autofillar1}
              className="form-control"
              readOnly
            />
          </div>
          <div className="mb-2">
            <label htmlFor="">Remise 2</label>
            <input
              type="text"
              value={remise2}
              placeholder="entrez la 2ème remise de cet article"
              className="form-control"
              onChange={(e) => setRemise2(e.target.value)}
            />
          </div>
          <div className="mb-2">
            <label htmlFor="">Prix d'achat AR2</label>
            <input
              type="text"
              value={autofillar2}
              className="form-control"
              readOnly
            />
          </div>
          <div className="mb-2">
            <label htmlFor="">Prix d'achat HT</label>
            <input
              type="text"
              value={achatttc}
              placeholder="entrez le HT de cet article"
              className="form-control"
              onChange={(e) => setAchatttc(e.target.value)}
            />
          </div>
          <div className="mb-2">
            <label htmlFor="">TVA</label><br />
            <select
                className="form-control"
                name="tva"
                value={selectedTva}
                onChange={(e) => setSelectedTva(e.target.value)}>
                <option value="0">0%</option>
                <option value="7">7%</option>
                <option value="13">13%</option>
                <option value="19">19%</option>
            </select>
          </div>
          <div className="mb-2">
            <label htmlFor="">Prix d'achatc TTC</label>
            <input
              type="text"
              placeholder="entrez le prix d'achat TTC"
              className="form-control"
              value={achatc}
              readOnly
            />
          </div>
          <div className="mb-2">
            <label htmlFor="">Marge bénéficiaire</label>
            <input
              type="text"
              placeholder="entrez la marge bénéficiaire désirée"
              className="form-control"
              onChange={(e) => setMbenif(e.target.value)}
            />
          </div>
          <div className="mb-2">
            <label htmlFor="">Prix de vente HT</label>
            <input
              type="text"
              placeholder="entrez le prix de vente HT"
              className="form-control"
              value={venteht}
              readOnly
            />
          </div>
          <div className="mb-2">
            <label htmlFor="">Prix de vente TTC</label>
            <input
              type="text"
              placeholder="entrez le prix de vente TTC"
              className="form-control"
              value={ventettc}
              readOnly
            />
          </div>
          <button onClick={handleRefresh} className="btn btn-success">Ajouter</button>
          </div>
        </form>
      </div>
    </div>
  );
}

export default CreateArticle;
