import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Modal from 'react-modal';
import CreateFactureVente from './CreateFactureVente';
import './Article.css'; 
import { FaSearch } from 'react-icons/fa';

Modal.setAppElement('#root'); 

function FactureVente() {
    const [fVente, setFVente] = useState([]);
    const [selectedFacture, setSelectedFacture] = useState(null);
    const [selectedRow, setSelectedRow] = useState(null);
    const [searchQuery, setSearchQuery] = useState('');
    const [modalIsOpen, setModalIsOpen] = useState(false);

    useEffect(() => {
        axios
            .get('http://localhost:7777/factureVente/')
            .then((res) => {
                const formattedFVente = res.data.map((client) => {
                    const date = new Date(client.date);
                    const options = { timeZone: 'Africa/Tunis' };
                    const formattedDate = date.toLocaleDateString('en-GB', options);
                    return {
                        ...client,
                        date: formattedDate,
                    };
                });
                setFVente(formattedFVente);
            })
            .catch((err) => console.log(err));
    }, []);

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:7777/factureVente/${id}`);
            setFVente(fVente.filter((facture) => facture.id !== id));
            setSelectedFacture(null);
            setSelectedRow(null);
        } catch (err) {
            console.log(err);
        }
    };

    const handleAddFVente = (newFVente) => {
        setFVente([...fVente, newFVente]);
    };

    const handleSearch = (query) => {
        setSearchQuery(query);
    };

    const fetchFactureDetails = (id) => {
        const url = `http://localhost:7777/factureVente/${id}`;
        console.log('Fetching details from URL:', url);
        axios
            .get(url)
            .then((res) => {
                setSelectedFacture(res.data);
                setModalIsOpen(true);
            })
            .catch((err) => console.log('Error fetching facture details:', err));
    };
    

    const filteredFVente = fVente.filter((client) => {
        return (
            client.client?.toLowerCase()?.includes(searchQuery.toLowerCase()) ||
            client.refAr?.toLowerCase()?.includes(searchQuery.toLowerCase())
        );
    });

    return (
        <div className="article-container">
            <CreateFactureVente onAddFVente={handleAddFVente} selectedFacture={selectedFacture} />
            <button onClick={() => setModalIsOpen(true)}>Voir les factures de vente</button>
            <Modal
                isOpen={modalIsOpen}
                onRequestClose={() => setModalIsOpen(false)}
                contentLabel="Voir les factures de vente"
                style={{
                    content: {
                        top: '50%',
                        left: '50%',
                        right: 'auto',
                        bottom: 'auto',
                        marginRight: '-50%',
                        transform: 'translate(-50%, -50%)',
                        width: '80%',
                        maxWidth: '800px',
                        maxHeight: '80vh', // Set max height for the modal content
                        overflowY: 'auto', // Enable vertical scrolling
                    },
                }}
            >
                {/* <button onClick={() => setModalIsOpen(false)}>Fermer</button> */}
                <div className="d-flex justify-content-between align-items-center mb-3">
                    <h2 className="mb-4">Factures de vente :</h2>
                    <div className="input-group">
                        <input
                            type="text"
                            className="form-control"
                            placeholder="Rechercher un client ou une référence..."
                            value={searchQuery}
                            onChange={(e) => handleSearch(e.target.value)}
                        />
                        <span className="input-group-text">
                            <FaSearch />
                        </span>
                    </div>
                </div>
                <div className="table-responsive">
                    <table className="table table-striped">
                        <thead>
                            <tr>
                                <th>Id</th>
                                <th>Nom du client</th>
                                <th>Total</th>
                            </tr>
                        </thead>
                        <tbody>
                            {filteredFVente.map((facture, index) => (
                                <tr
                                    key={facture.id}
                                    onClick={() => {
                                        setSelectedRow(index);
                                        fetchFactureDetails(facture.id);
                                    }}
                                    className={selectedRow === index ? 'table-active' : ''}
                                    style={{ cursor: 'pointer' }}
                                >
                                    <td>{facture.id}</td>
                                    <td>{facture.client}</td>
                                    <td>{facture.totalPrice}</td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </div>
            </Modal>
        </div>
    );
}

export default FactureVente;
