import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

function CreateFactureAchat({ selectedFacture }) {
  const [clName, setClName] = useState('');
  const [client_id, setClientCode] = useState('');
  const [date, setDate] = useState('');
  const [DT, setDT] = useState('1.000');
  const [inputs, setInputs] = useState([{ refAr: '', quantité: '', price: 0, remise: 0 }]);
  const [references, setReferences] = useState([]);
  const [filteredClients, setFilteredClients] = useState([]);

  const remiseInputRef = useRef(null);

  useEffect(() => {
    axios.get('http://localhost:7777/article')
      .then(res => {
        setReferences(res.data.map(article => article.ref));
      })
      .catch(err => console.log('Error fetching articles:', err));
  }, []);

  useEffect(() => {
    if (selectedFacture) {
      console.log('Selected Facture in CreateFactureAchat:', selectedFacture);
      setClName(selectedFacture.clName || '');
      setClientCode(selectedFacture.client_id || '');
      setDate(selectedFacture.date || '');
      setDT(selectedFacture.DT || '1.000');
      setInputs(selectedFacture.articles || [{ refAr: '', quantité: '', price: 0, remise: 0 }]);
    }
  }, [selectedFacture]);

  const fetchPriceByReference = (reference, index) => {
    axios.get(`http://localhost:7777/factureAchat/${reference}`)
      .then(res => {
        const price = res.data.price;
        const newInputs = [...inputs];
        newInputs[index].price = price;
        setInputs(newInputs);
      })
      .catch(err => console.log('Error fetching price:', err));
  };

  const handleReferenceChange = (event, index) => {
    const { value } = event.target;
    const newInputs = [...inputs];
    newInputs[index].refAr = value;
    setInputs(newInputs);
    fetchPriceByReference(value, index);
  };

  const handleRemiseChange = (event, index) => {
    const { value } = event.target;
    const newInputs = [...inputs];
    newInputs[index].remise = value;
    setInputs(newInputs);
  };

  const handleQuantityChange = (event, index) => {
    const { value } = event.target;
    const newInputs = [...inputs];
    newInputs[index].quantité = value;
  
    // Check if value is a valid number
    if (!isNaN(value) && value !== '') {
      axios.get(`http://localhost:7777/article/${newInputs[index].refAr}/quantity`)
        .then(res => {
          const availableQuantity = res.data.quantity;
          const remainingQuantity = availableQuantity - parseInt(value);
  
          if (remainingQuantity < 0) {
            alert(`La quantité souhaitée n'est pas suffisante, vous n'avez que ${availableQuantity} pièces de cet article`);
            newInputs[index].quantité = availableQuantity; // Setting the input quantity to available quantity
          }
  
          setInputs(newInputs);
        })
        .catch(err => console.error('Error fetching quantity:', err));
    } else {
      setInputs(newInputs);
    }
  };

  const handleDTChange = (event) => {
    const value = event.target.value;
    const validValue = value.match(/^\d*(\.\d{0,3})?$/); // Regex to allow up to three decimal places
    if (validValue) {
      setDT(value);
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    const formattedInputs = inputs.map(input => ({
      ...input,
      quantité: parseFloat(input.quantité),
      price: parseFloat(input.price),
      remise: parseFloat(input.remise)
    }));

    const data = {
      clName,
      client_id,
      date,
      DT,
      articles: formattedInputs,
      totalPrice: calculateTotalPrice()
    };

    axios.post('http://localhost:7777/factureAchat', data)
      .then(response => {
        console.log(response.data);
        // Reset form fields after successful submission
        setClName('');
        setClientCode('');
        setDate('');
        setDT('1.000');
        setInputs([{ refAr: '', quantité: '', price: 0, remise: 0 }]);
      })
      .catch(error => {
        console.error('There was an error!', error);
      });
  };

  const addInput = () => {
    setInputs([...inputs, { refAr: '', quantité: '', price: 0, remise: 0 }]);
  };

  const calculateTotalPrice = () => {
    let total = 0;
    inputs.forEach(input => {
      const itemTotal = input.quantité * input.price * DT * (1 - input.remise / 100);
      total += itemTotal;
    });
    return total.toFixed(3);
  };

  const handleClientChange = async (e) => {
    const value = e.target.value;
    setClName(value);

    if (value.trim() === '') {
      setFilteredClients([]);
      return;
    }

    try {
      const response = await axios.get('http://localhost:7777/clients');
      const clients = response.data;
      const filtered = clients.filter(client =>
        client.nom && client.nom.toLowerCase().includes(value.toLowerCase())
      );
      setFilteredClients(filtered);
    } catch (error) {
      console.error('Error fetching clients:', error);
      setFilteredClients([]);
    }
  };

  const handleClientSelect = (client) => {
    setClName(client.nom);
    setClientCode(client.id);
    setFilteredClients([]);
  };

  return (
    <div className="container mt-4">
      <form onSubmit={handleSubmit} className="form-container">
        <div className="form-group">
          <label>Nom du 3asba:</label>
          <input
            type="text"
            className="form-control"
            value={clName}
            onChange={handleClientChange}
            required
          />
          {filteredClients.length > 0 && (
            <div className="autocomplete-dropdown">
              {filteredClients.map(client => (
                <div
                  key={client.id}
                  onClick={() => handleClientSelect(client)}
                  className="autocomplete-item"
                >
                  {client.nom}
                </div>
              ))}
            </div>
          )}
        </div>
        <div className="form-group">
          <label>Client Code:</label>
          <input
            type="text"
            className="form-control"
            value={client_id}
            onChange={(e) => setClientCode(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Date:</label>
          <input
            type="date"
            className="form-control"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>DT:</label>
          <input
            type="text"
            className="form-control"
            value={DT}
            onChange={handleDTChange}
            required
          />
        </div>
        {inputs.map((input, index) => (
          <div key={index} className="form-group">
            <label>Référence de l'article:</label>
            <select
              className="form-control"
              value={input.refAr}
              onChange={(event) => handleReferenceChange(event, index)}
              required
            >
              <option value="">Choisir une référence</option>
              {references.map(reference => (
                <option key={reference} value={reference}>
                  {reference}
                </option>
              ))}
            </select>
            <label>Quantité:</label>
            <input
              type="number"
              className="form-control"
              value={input.quantité}
              onChange={(event) => handleQuantityChange(event, index)}
              required
            />
            <label>Prix:</label>
            <input
              type="number"
              className="form-control"
              value={input.price}
              readOnly
            />
            <label>Remise (%):</label>
            <input
              type="number"
              className="form-control"
              value={input.remise}
              ref={remiseInputRef}
              onChange={(event) => handleRemiseChange(event, index)}
            />
          </div>
        ))}
        <button type="button" className="btn btn-secondary" onClick={addInput}>
          Ajouter un article
        </button>
        <button type="submit" className="btn btn-primary">
          Enregistrer
        </button>
        <div className="mt-3">
          <h5>Prix Total: {calculateTotalPrice()} DT</h5>
        </div>
      </form>
    </div>
  );
}

export default CreateFactureAchat;
