import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import Modal from 'react-modal';
import 'bootstrap/dist/css/bootstrap.min.css';

Modal.setAppElement('#root');

function Cfvbs({ onFactureSubmit, selectedFacture }) {
  const [inputs, setInputs] = useState([{ refAr: '', quantité: '', price: 0, remise: 0 }]);
  const [references, setReferences] = useState([]);

  const remiseInputRef = useRef(null);

  useEffect(() => {
    axios.get('http://localhost:7777/article')
      .then(res => {
        setReferences(res.data.map(article => article.ref));
      })
      .catch(err => console.log('Error fetching articles:', err));
  }, []);

  useEffect(() => {
    if (selectedFacture) {
      console.log('Selected Facture in CreateFactureVente:', selectedFacture);
      setInputs(selectedFacture.articles || [{ refAr: '', quantité: '', price: 0, remise: 0 }]);
    }
  }, [selectedFacture]);

  const fetchPriceByReference = (reference, index) => {
    axios.get(`http://localhost:7777/cfvbs/${reference}`)
      .then(res => {
        const price = res.data.price;
        const newInputs = [...inputs];
        newInputs[index].price = price;
        setInputs(newInputs);
      })
      .catch(err => console.log('Error fetching price:', err));
  };

  const handleReferenceChange = (event, index) => {
    const { value } = event.target;
    const newInputs = [...inputs];
    newInputs[index].refAr = value;
    setInputs(newInputs);
    fetchPriceByReference(value, index);
  };

  const handleRemiseChange = (event, index) => {
    const { value } = event.target;
    const newInputs = [...inputs];
    newInputs[index].remise = value;
    setInputs(newInputs);
  };

  const handleQuantityChange = (event, index) => {
    const { value } = event.target;
    const newInputs = [...inputs];
    newInputs[index].quantité = value;

    if (!isNaN(value) && value !== '') {
      axios.get(`http://localhost:7777/article/${newInputs[index].refAr}/quantity`)
        .then(res => {
          const availableQuantity = res.data.quantity;
          const remainingQuantity = availableQuantity - parseInt(value);

          if (remainingQuantity < 0) {
            alert(`La quantité souhaitée n'est pas suffisante, vous n'avez que ${availableQuantity} pièces de cet article`);
            newInputs[index].quantité = availableQuantity; 
          }

          setInputs(newInputs);
        })
        .catch(err => console.error('Error fetching quantity:', err));
    } else {
      alert('Veuillez entrer une quantité valide.');
    }
  };

  const handleAddInput = async () => {
    const isEmpty = inputs.some(input => input.refAr === '' || input.quantité === '' );
    if (isEmpty) {
      alert("Veuillez remplir tous les champs de la ligne précédente avant d'ajouter une nouvelle ligne.");
      return;
    }
    setInputs([...inputs, { refAr: '', price: 0, quantité: '', remise: 0 }]);
  };

  const handleSubmit = async () => {
    try {
      const articles = [...inputs];
      await axios.post('http://localhost:7777/ssortievente', { articles });
      alert("Les articles ont été ajoutés avec succès!");
      window.location.reload();
    } catch (error) {
      console.error('Error posting to ssortievente:', error);
      alert("Une erreur s'est produite lors de l'ajout des articles.");
    }
  }

  const updateStock = async (inputs) => {
    try {
      for (const input of inputs) {
        if (!isNaN(input.quantité) && input.quantité !== '') {
          const response = await axios.put(`http://localhost:7777/article/${input.refAr}/updateStock`, { quantity: input.quantité });
          console.log(`Stock updated for ${input.refAr}:`, response.data);
        }
      }
    } catch (error) {
      console.error('Error updating stock:', error);
    }
  };

  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      handleAddInput();
    }
  };

  const handleClear = () => {
    setInputs([{ refAr: '', quantité: '', price: 0, remise: 0 }]);
  };

  const calculateTotal = () => {
    return inputs.reduce((total, input) => {
      const quantity = parseFloat(input.quantité) || 0;
      const price = parseFloat(input.price) || 0;
      const remise = parseFloat(input.remise) || 0;
      const lineTotal = (quantity * price) * (1 - remise / 100);
      return total + lineTotal;
    }, 0).toFixed(2);
  };

  const handleClick = async () => {
    await handleAddInput();
    updateStock(inputs);
  };

  return (
    <div style={{ margin: '20px', justifyContent: 'center' }}>
      <div style={{ display: 'flex', flex: '1' }}>
        <div>
          {inputs.map((input, index) => (
            <div key={index} style={{ display: 'flex', alignItems: 'center', marginBottom: '10px' }}>
              <div style={{ flex: '1', marginRight: '10px' }}>
                <label>Référence d'article</label>
                <input
                  list="references"
                  style={{ width: '100%' }}
                  value={input.refAr}
                  onChange={(e) => handleReferenceChange(e, index)}
                />
                <datalist id="references">
                  {references.map((reference, idx) => (
                    <option key={idx} value={reference} />
                  ))}
                </datalist>
              </div>
              <div style={{ flex: '1', marginRight: '10px' }}>
                <label>Prix vente TTC</label>
                <input
                  type="number"
                  style={{ width: '100%' }}
                  value={input.price}
                  readOnly
                />
              </div>
              <div style={{ flex: '1', marginRight: '10px' }}>
                <label>Quantité</label>
                <input
                  type="number"
                  style={{ width: '100%' }}
                  value={input.quantité}
                  onChange={(e) => handleQuantityChange(e, index)}
                />
              </div>
              <div style={{ display: 'flex', alignItems: 'center' }}>
                <div style={{ flex: '1' }}>
                  <label>Remise</label>
                  <input
                    type="number"
                    style={{ width: '100%' }}
                    value={input.remise}
                    onChange={(e) => handleRemiseChange(e, index)}
                    onKeyDown={handleKeyPress}
                    ref={remiseInputRef}
                  />
                </div>
                <div style={{ marginLeft: '10px' }}>
                  <button type="button" onClick={handleAddInput}>+</button>
                </div>
              </div>
            </div>
          ))}
        </div>
        <div>
          <div style={{ display: "inline-block", marginBottom: '20px', textAlign: 'center' }}>
            <button
              style={{ width: '200px', marginBottom: '10px' }}
              type="button"
              onClick={handleSubmit}
            >
              Ajouter
            </button>
            <button
              style={{ width: '200px', marginBottom: '10px' }}
              type="button"
              onClick={handleClear}
            >
              Vider les champs
            </button>
          </div>
        </div>
      </div>
      <div style={{ marginBottom: '20px' }}>
        <label>Total</label>
        <input
          style={{ width: '100%' }}
          value={calculateTotal()}
          readOnly
        />
      </div>
    </div>
  );
}

function Fvbs() {
  const [fVente, setFVente] = useState([]);
  const [ssortieVente, setSSortieVente] = useState([]);
  const [selectedFacture, setSelectedFacture] = useState(null);
  const [selectedRowId, setSelectedRowId] = useState(null);
  const [modalIsOpen, setModalIsOpen] = useState(false);

  useEffect(() => {
    axios.get('http://localhost:7777/fvbs/')
      .then((res) => {
        const formattedFVente = res.data.map((facture) => {
          const date = new Date(facture.date);
          const formattedDate = date.toISOString().split('T')[0];
          return {
            ...facture,
            date: formattedDate,
          };
        });
        setFVente(formattedFVente);
      })
      .catch((err) => console.log(err));

    axios.get('http://localhost:7777/ssortievente')
      .then((res) => {
        const parsedData = res.data.map(entry => ({
          ...entry,
          articles: JSON.parse(entry.articles)
        }));
        setSSortieVente(parsedData);
      })
      .catch((err) => console.log('Error fetching ssortievente data:', err));
  }, []);

  const handleRowClick = (id, articles) => {
    setSelectedFacture({ articles });
    setSelectedRowId(id);
  };

  const handleDelete = async () => {
    const id = selectedRowId;
    if (!id) return;

    try {
      await axios.delete(`http://localhost:7777/ssortievente/${id}`);
      setSSortieVente(ssortieVente.filter((_, index) => index !== id));
      setSelectedFacture(null);
      setSelectedRowId(null);
    } catch (err) {
      console.log(err);
    }
  };
  const handleSubmit = async () => {
  try {
    const articles = [...inputs]; // Make sure to send a copy of the inputs array
    await axios.post('http://localhost:7777/ssortievente', { articles });
    alert("Les articles ont été ajoutés avec succès!");
    window.location.reload();
  } catch (error) {
    console.error('Error posting to ssortievente:', error);
    alert("Une erreur s'est produite lors de l'ajout des articles.");
  }
};


  return (
    <div className="facture-vente-container">
      <Cfvbs onFactureSubmit={handleSubmit} selectedFacture={selectedFacture} />
      <button onClick={() => setModalIsOpen(true)}>Voir les produits</button>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={() => setModalIsOpen(false)}
        contentLabel="Voir les factures de vente"
        style={{
          content: {
            top: '50%',
            left: '50%',
            right: 'auto',
            bottom: 'auto',
            marginRight: '-50%',
            transform: 'translate(-50%, -50%)',
            width: '80%',
            maxWidth: '800px',
            maxHeight: '80vh',
            overflowY: 'auto',
          },
        }}
      >
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h2 className="mb-4">Factures de vente :</h2>
        </div>
        <button onClick={handleDelete}>Supprimer</button>
        <div className="table-responsive">
          <table className="table table-striped">
            <thead>
              <tr>
                <th>Reference</th>
                <th>Prix</th>
                <th>Quantité</th>
                <th>Remise</th>
              </tr>
            </thead>
            <tbody>
              {ssortieVente.map((entry, entryIndex) => {
                const articles = entry.articles;
                return articles.map((article, articleIndex) => {
                  const id = `${entryIndex}-${articleIndex}`;
                  return (
                    <tr
                      key={id}
                      onClick={() => handleRowClick(entryIndex, articles)}
                      className={selectedRowId === entryIndex ? 'table-active' : ''}
                      style={{ cursor: 'pointer' }}
                    >
                      <td>{article.refAr}</td>
                      <td>{article.price}</td>
                      <td>{article.quantité}</td>
                      <td>{article.remise}</td>
                    </tr>
                  );
                });
              })}
            </tbody>
          </table>
          {/* <button onClick={handleCreateFactureClick}>Créer facture</button> */}
        </div>
      </Modal>
    </div>
  );
}

export default Fvbs;
