import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Modal from 'react-modal';
import Cfvbs from './Cfvbs';
import { FaSearch } from 'react-icons/fa';

Modal.setAppElement('#root');

function Fvbs() {
  const [fVente, setFVente] = useState([]);
  const [ssortieVente, setSSortieVente] = useState([]);
  const [selectedFacture, setSelectedFacture] = useState(null);
  const [selectedRow, setSelectedRow] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [modalIsOpen, setModalIsOpen] = useState(false);

  useEffect(() => {
    axios
      .get('http://localhost:7777/fvbs/')
      .then((res) => {
        const formattedFVente = res.data.map((facture) => {
          const date = new Date(facture.date);
          const formattedDate = date.toISOString().split('T')[0];
          return {
            ...facture,
            date: formattedDate,
          };
        });
        setFVente(formattedFVente);
      })
      .catch((err) => console.log(err));

    axios
      .get('http://localhost:7777/ssortievente')
      .then((res) => {
        setSSortieVente(res.data);
      })
      .catch((err) => console.log('Error fetching ssortievente data:', err));
  }, []);

  const handleDelete = async () => {
    const id = selectedFacture?.id;
    if (!id) return;
    try {
      await axios.delete(`http://localhost:7777/factureVente/${id}`);
      setFVente(fVente.filter((facture) => facture.id !== id));
      setSelectedFacture(null);
      setSelectedRow(null);
    } catch (err) {
      console.log(err);
    }
  };

  const handleAddFactureVente = (newFactureVente) => {
    setFVente([...fVente, newFactureVente]);
  };

  const filteredFVente = fVente.filter((facture) => {
    return (
      facture.clName?.toLowerCase()?.includes(searchQuery.toLowerCase()) ||
      facture.articles.some((article) =>
        article.refAr.toLowerCase().includes(searchQuery.toLowerCase())
      )
    );
  });

  const handleSearch = (query) => {
    setSearchQuery(query);
  };

  const handleSubmit = (newFactureData) => {
    axios
      .post('http://localhost:7777/cfvbs/', newFactureData)
      .then((res) => {
        setModalIsOpen(false);
        setFVente([...fVente, res.data]);
      })
      .catch((err) => console.log('Error creating facture:', err));
  };

  const handleRowClick = (entryId, index) => {
    const selectedArticle = ssortieVente[entryId].articles[index];
    setSelectedFacture({
      articles: [
        {
          refAr: selectedArticle.refAr,
          quantité: selectedArticle.quantité,
          price: selectedArticle.price,
          remise: selectedArticle.remise,
        },
      ],
    });
    setSelectedRow(`${entryId}-${index}`);
  };

  return (
    <div className="facture-vente-container">
      <Cfvbs onFactureSubmit={handleSubmit} selectedFacture={selectedFacture} />
      <button onClick={() => setModalIsOpen(true)}>Voir les produits</button>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={() => setModalIsOpen(false)}
        contentLabel="Voir les factures de vente"
        style={{
          content: {
            top: '50%',
            left: '50%',
            right: 'auto',
            bottom: 'auto',
            marginRight: '-50%',
            transform: 'translate(-50%, -50%)',
            width: '80%',
            maxWidth: '800px',
            maxHeight: '80vh',
            overflowY: 'auto',
          },
        }}
      >
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h2 className="mb-4">Factures de vente :</h2>
          <div className="input-group">
            <input
              type="text"
              className="form-control"
              placeholder="Rechercher un client ou une référence..."
              value={searchQuery}
              onChange={(e) => handleSearch(e.target.value)}
            />
            <span className="input-group-text">
              <FaSearch />
            </span>
          </div>
        </div>
        <button onClick={handleDelete}>Supprimer</button>
        <div className="table-responsive">
          <table className="table table-striped">
            <thead>
              <tr>
                <th>Reference</th>
                <th>Prix</th>
                <th>Quantité</th>
                <th>Remise</th>
              </tr>
            </thead>
            <tbody>
              {ssortieVente.map((entry, entryIndex) => {
                const articles = JSON.parse(entry.articles);
                return articles.map((article, index) => (
                  <tr
                    key={`${entry.id}-${index}`}
                    onClick={() => {
                      handleRowClick(entryIndex, index);
                    }}
                    className={selectedRow === `${entryIndex}-${index}` ? 'table-active' : ''}
                    style={{ cursor: 'pointer' }}
                  >
                    <td>{article.refAr}</td>
                    <td>{article.price}</td>
                    <td>{article.quantité}</td>
                    <td>{article.remise}</td>
                  </tr>
                ));
              })}
            </tbody>
          </table>
        </div>
      </Modal>
    </div>
  );
}

export default Fvbs;
