import React, {useEffect, useState} from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import CreateFactureAchat from './CreateFactureAchat';


function FactureAchat() {
    const [fAchat,setFAchat] = useState([])
    useEffect(() => {
        axios.get('http://localhost:7777/factureAchat/')
        .then(res => setFAchat(res.data))
        .catch(err => console.log(err));
    }, [])

    const handleDelete = async (id) => {
        try {
            await axios.delete('http://localhost:7777/factureAchat/'+id)
            window.location.reload()
        } catch(err){
            console.log(err);
        }
    }
    const handleAddFAchat = (newFAchat) => {
        setFAchat([...fAchat, newFAchat]);
    }
  return (
    <div className='article-container'>
        <div className='w-110 bg-white rounded p-3'>
            <CreateFactureAchat onAddFAchat= {handleAddFAchat} /><br />
            <div className='table-responsive'>
            <table className='table table-striped'>
                <thead>
                    <tr>
                    <th>Id</th>
                    <th>Nom du fournisseur</th>
                    <th>Date</th>
                    <th>Code</th>
                    <th>Ref Article</th>
                    <th>Quantité</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        fAchat.map((data,i) => (
                            <tr key={i}>
                                <td>{data.idfactureAchat}</td>
                                <td>{data.fss}</td>
                                <td>{data.date}</td>
                                <td>{data.code}</td>
                                <td>{data.refArticle}</td>
                                <td>{data.quantité}</td>
                                <td>
                                    <Link to={`updateCl/${data.clID}`} className='btn btn-primary'>Changer</Link>
                                    <button className='btn btn-danger ms-2' onClick={e => handleDelete(data.clID)}>Supprimer</button>
                                </td>
                            </tr>
                        ))
                    }
                </tbody>
            </table>
            </div>
        </div>
    </div>
  )
}

export default FactureAchat;
