import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import CreateFactureVente from './CreateFactureVente'

function FactureVente() {
    const [fVente, setFVente] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:7777/factureVente/')
            .then(res => {
                // Format the date for each client object
                const forfVente = res.data.map(client => {
                    const date = new Date(client.date);
                    // Create options object to specify the timezone as 'Africa/Tunis'
                    const options = { timeZone: 'Africa/Tunis' };
                    // Format the date using toLocaleDateString() with the options
                    const formattedDate = date.toLocaleDateString('en-GB', options);
                    return {
                        ...client,
                        date: formattedDate
                    };
                });
                setFVente(forfVente);
            })
            .catch(err => console.log(err));
    }, []);
    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:7777/factureVente/${id}`);
            window.location.reload();
        } catch (err) {
            console.log(err);
        }
    };
    const handleAddFVente = (newFVente) => {
        setFVente([...fVente, newFVente]);
    }
    return (
        <div className='article-container'>
            <div className='w-110 bg-white rounded p-3'>
                <CreateFactureVente onAddFVente={handleAddFVente}/><br />
                <h2 className="mb-4">Liste des factures de vente</h2>
                <div className="input-group mb-3">
        <input
          type="text"
          className="form-control"
          placeholder="Rechercher un client..."
        //   value={searchQuery}
        //   onChange={(e) => setSearchQuery(e.target.value)}
        />
                <div className='table-responsive'>
                <table className='table table-striped'>
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>Nom du client</th>
                            <th>Date</th>
                            <th>Code</th>
                            <th>Ref Article</th>
                            <th>Quantité</th>
                        </tr>
                    </thead>
                    <tbody>
                        {fVente.map((client, i) => (
                            <tr key={i}>
                                <td>{client.id}</td>
                                <td>{client.client}</td>
                                <td>{client.date}</td>
                                <td>{client.code}</td>
                                <td>{client.refArticle}</td>
                                <td>{client.quantité}</td>
                                <td>
                                    <Link to={`updateCl/${client.clID}`} className='btn btn-primary'>Changer</Link>
                                    <button className='btn btn-danger ms-2' onClick={e => handleDelete(client.clID)}>Supprimer</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
                </div>
            </div>
        </div>
        </div>
    );
}

export default FactureVente;
