import axios from "axios";
import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";

function CreateArticle() {
  const [name, setName] = useState("");
  const [ref, setRef] = useState("");
  const [achatbrut, setAchatbrut] = useState("");
  const [remise1, setRemise1] = useState("");
  const [achatar1, setAchatar1] = useState("");
  const [remise2, setRemise2] = useState("");
  const [achatar2, setAchatar2] = useState("");
  const [tva, setTva] = useState("");
  const [achatttc, setAchatttc] = useState("");
  const [mbenif, setMbenif] = useState("");
  const [venteht, setVenteht] = useState("");
  const [ventettc, setVentettc] = useState("");
  const [autofillar1, setAutofillar1] = useState('');

  const navigate = useNavigate();

  useEffect(() => {
    // Check if both achatbrut and remise1 have values
    if (achatbrut && remise1) {
      // Calculate the sum of achatbrut and remise1
      const sum = parseFloat(achatbrut) / 100 * (100 -parseFloat(remise1));
      // Set the sum as the value of autofillar1
      setAutofillar1(sum.toString());
    } else {
      // Clear autofillar1 if any of the inputs are empty
      setAutofillar1('');
    }
  }, [achatbrut, remise1]);

  useEffect(() => {
    // Check if both achatbrut and remise1 have values
    if (achatar1 && remise2) {
      // Calculate the sum of achatbrut and remise1
      const sum = parseFloat(achatar1) / 100 * (100 -parseFloat(remise2));
      // Set the sum as the value of autofillar1
      setAutofillar1(sum.toString());
    } else {
      // Clear autofillar1 if any of the inputs are empty
      setAutofillar2('');
    }
  }, [achatbrut, remise1]);

  function handleSubmit(event) {
    event.preventDefault();
    axios
      .post("http://localhost:7777/article/createAr/", {
        arName: name,
        ref: ref,
        prachat: achatbrut,
        remise1: remise1,
        prar1: achatar1,
        remise2: remise2,
        prar2: achatar2,
        tva: tva,
        achatttc: achatttc,
        margeben: mbenif,
        prventeht: venteht,
        prventettc: ventettc,
      })
      .then((res) => {
        console.log(res);
        navigate("/article/");
      })
      .catch((err) => console.log(err));
  }

  return (
    <div className="d-flex justify-content-center align-items-center">
      <div className="w-50 bg-white rounded p-3">
        <form onSubmit={handleSubmit}>
          <h2>Ajouter un article</h2>
          <div className="mb-2">
            <label htmlFor="">Nom d'article</label>
            <input
              type="text"
              placeholder="entrez le nom d'article"
              className="form-control"
              onChange={(e) => setName(e.target.value)}
            />
          </div>
          <div className="mb-2">
            <label htmlFor="">Référence</label>
            <input
              type="text"
              placeholder="entrez la référence de cet article"
              className="form-control"
              onChange={(e) => setRef(e.target.value)}
            />
          </div>
          <div className="mb-2">
            <label htmlFor="">Prix d'achat brut</label>
            <input
              type="number"
              value={achatbrut}
              placeholder="entrez le prix d'achat brut de cet article"
              className="form-control"
              onChange={(e) => setAchatbrut(e.target.value)}
            />
          </div>
          <div className="mb-2">
            <label htmlFor="">Remise 1</label>
            <input
              type="number"
              value={remise1}
              placeholder="entrez la 1ère remise de cet article"
              className="form-control"
              onChange={(e) => setRemise1(e.target.value)}
            />
          </div>
          <div className="mb-2">
            <label htmlFor="">Prix d'achat AR1</label>
            <input
              type="text"
              value={autofillar1}
              className="form-control"
              readOnly
              onChange={(e) => setAchatar1(e.target.value)}
            />
          </div>
          <div className="mb-2">
            <label htmlFor="">Remise 2</label>
            <input
              type="text"
              placeholder="entrez la 2ème remise de cet article"
              className="form-control"
              onChange={(e) => setRemise2(e.target.value)}
            />
          </div>
          <div className="mb-2">
            <label htmlFor="">Prix d'achat AR2</label>
            <input
              type="text"
              placeholder="entrez le prix d'achat après la 2ème remise"
              className="form-control"
              readOnly
              onChange={(e) => setAchatar2(e.target.value)}
            />
          </div>
          <div className="mb-2">
            <label htmlFor="">TVA</label><br />
            <select className="form-control" name="tva" onChange={e => setTva(e.target.value)}>
              <option value="0">0%</option>
              <option value="7">7%</option>
              <option value="13">13%</option>
              <option value="19">19%</option>
            </select><br />
          </div>
          <div className="mb-2">
            <label htmlFor="">Prix TTC</label>
            <input
              type="text"
              value={achatttc}
              placeholder="entrez le TTC de cet article"
              className="form-control"
              onChange={(e) => setAchatttc(e.target.value)}
            />
          </div>
          <div className="mb-2">
            <label htmlFor="">Marge bénéficiaire</label>
            <input
              type="text"
              placeholder="entrez la marge bénéficiaire désirée"
              className="form-control"
              onChange={(e) => setMbenif(e.target.value)}
            />
          </div>
          <div className="mb-2">
            <label htmlFor="">Prix de vente HT</label>
            <input
              type="text"
              placeholder="entrez le prix de vente HT"
              className="form-control"
              onChange={(e) => setVenteht(e.target.value)}
            />
          </div>
          <div className="mb-2">
            <label htmlFor="">Prix de vente TTC</label>
            <input
              type="text"
              placeholder="entrez le prix de vente HT"
              className="form-control"
              onChange={(e) => setVentettc(e.target.value)}
            />
          </div>
          <button className="btn btn-success">Ajouter</button>
        </form>
      </div>
    </div>
  );
}

export default CreateArticle;
