import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function CreateFactureVente() {
    const [client, setClient] = useState('');
    const [date, setDate] = useState('');
    const [code, setCode] = useState('');
    const [refAr, setRefArticle] = useState('');
    const [quantité, setQuantité] = useState('');
    const [references, setReferences] = useState([]);
    const [price, setPrice] = useState(0);

    useEffect(() => {
        axios.get('http://localhost:7777/references')
            .then(res => {
                setReferences(res.data);
            })
            .catch(err => console.log(err));
    }, []);

    function handleSubmit(event) {
        event.preventDefault();
        // Calculate total price
        const totalPrice = quantité * price;
        axios.post('http://localhost:7777/factureVente/createFac/', {
            client: client,
            date: date,
            code: code,
            refAr: refAr,
            quantité: quantité,
            totalPrice: totalPrice  // Add total price to the request
        })
        .then(res => {
            console.log(res);
            window.location.reload();
        })
        .catch(err => console.log(err))
    }

    // Handle change in quantity input
    function handleQuantityChange(event) {
        setQuantité(event.target.value);
        // Update price based on selected article and quantity
        const selectedRef = event.target.value;
        const selectedArticle = references.find(article => article.refArticle === selectedRef);
        if (selectedArticle) {
            setPrice(selectedArticle.price);
        }
    }

    return (
        <div className='container mt-5'>
            <h2 className="mb-4">Ajouter une facture de vente</h2>
            <form onSubmit={handleSubmit}>
                <div className="row mb-3">
                    <div className='col'>
                        <label htmlFor="">Nom du client</label>
                        <input type='text' placeholder='Entrez le nom du client' className='form-control'
                            onChange={e => setClient(e.target.value)}
                        />
                    </div>
                    <div className='col'>
                        <label htmlFor="">Date</label>
                        <input type="date" className='form-control'
                            onChange={e => setDate(e.target.value)}
                        />
                    </div>
                </div>
                <div className="row mb-3">
                    <div className='col'>
                        <label htmlFor="">Code de la facture</label>
                        <input type='text' placeholder='Entrez le code de la facture' className='form-control'
                            onChange={e => setCode(e.target.value)}
                        />
                    </div>
                    <div className='col'>
                        <label htmlFor="">Référence d'article</label>
                        <input
                            list="references"
                            className='form-control'
                            placeholder="Select or type reference..."
                            value={refArticle}
                            onChange={(e) => setRefArticle(e.target.value)}
                        />
                        <datalist id="references">
                            {references.map((reference, index) => (
                                <option key={index} value={reference.refArticle} />
                            ))}
                        </datalist>
                    </div>
                </div>
                <div className="row mb-3">
                    <div className='col'>
                        <label htmlFor="">Quantité</label>
                        <input type='number' placeholder='Entrez la quantité désirée' className='form-control'
                            value={quantité}
                            onChange={handleQuantityChange}
                        />
                    </div>
                    <div className='col'>
                        <label htmlFor="">Total</label>
                        <input type='number' placeholder='Total de la facture' className='form-control'
                            value={quantité * price}  // Display calculated total
                            disabled  // Disable input since it's a calculated value
                        />
                    </div>
                </div>
                <button className='btn btn-success'>Ajouter</button>
            </form>
        </div>
    );
}

export default CreateFactureVente;
