import React, { useEffect, useState } from 'react';
import axios from 'axios';
import CreateArticle from './CreateArticle';
import { TiPlus } from 'react-icons/ti'; // Import the plus icon from React-icons

function Article() {
    const [articles, setArticles] = useState([]);
    const [additionalColumns, setAdditionalColumns] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:7777/article/')
            .then(res => setArticles(res.data))
            .catch(err => console.log(err));
    }, []);

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:7777/article/${id}`);
            setArticles(articles.filter(article => article.arID !== id));
        } catch (err) {
            console.log(err);
        }
    }

    const handleAddArticle = (newArticle) => {
        setArticles([...articles, newArticle]);
    }

    const toggleColumn = (columnName) => {
        if (additionalColumns.includes(columnName)) {
            setAdditionalColumns(additionalColumns.filter(col => col !== columnName));
        } else {
            setAdditionalColumns([...additionalColumns, columnName]);
        }
    }

    return (
        <div className='article-container'>
            <CreateArticle onAddArticle={handleAddArticle} /><br />
            <h2>Liste des Articles</h2>
            <div className='table-responsive'>
                <table className='table table-striped'>
                    <thead>
                        <tr>
                            <th>Id</th> 
                            <th>Nom</th>
                            <th>Référence</th>
                            <th>TVA</th>
                            <th>Prix TTC</th>
                            <th>Vente TTC</th>
                            {additionalColumns.map(column => (
                                <th key={column}>
                                    <span onClick={() => toggleColumn(column)}>
    <TiThumbsUp /> {column}
</span>
                                </th>
                            ))}
                        </tr>
                    </thead>
                    <tbody>
                        {articles.map(article => (
                            <tr key={article.arID}>
                                <td>{article.arID}</td>
                                <td>{article.arName}</td>
                                <td>{article.ref}</td>
                                <td>{article.tva}%</td>
                                <td>{article.achatc}</td>
                                <td>{article.prventettc}</td>
                                {additionalColumns.map(column => (
                                    <td key={column}>{article[column]}</td>
                                ))}
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default Article;





