import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Modal from 'react-modal';
import CreateFactureVente from './CreateFactureVente';
import { FaSearch } from 'react-icons/fa';

Modal.setAppElement('#root');

function FactureAchat() {
  const [fAchat, setFAchat] = useState([]);
  const [selectedFacture, setSelectedFacture] = useState(null);
  const [selectedRow, setSelectedRow] = useState(null);
  const [searchQuery, setSearchQuery] = useState('');
  const [modalIsOpen, setModalIsOpen] = useState(false);

  useEffect(() => {
    axios
      .get('http://localhost:7777/factureAchat/')
      .then((res) => {
        const formattedFAchat = res.data.map((facture) => {
          const date = new Date(facture.date);
          const formattedDate = date.toISOString().split('T')[0]; // Ensure date is in YYYY-MM-DD format
          return {
            ...facture,
            date: formattedDate,
          };
        });
        setFAchat(formattedFAchat);
      })
      .catch((err) => console.log(err));
  }, []);

  // const handleDelete = async () => {
  //   const id = selectedFacture?.id;
  //   if (!id) return; // Exit early if no facture is selected
  //   try {
  //     await axios.delete(`http://localhost:7777/factureVente/${id}`);
  //     setFAchat(fAchat.filter((facture) => facture.id !== id));
  //     setSelectedFacture(null); // Reset selectedFacture after deletion
  //     setSelectedRow(null);
  //   } catch (err) {
  //     console.log(err);
  //   }
  // };

  const handleAddFactureVente = (newFactureVente) => {
    setFAchat([...fAchat, newFactureVente]);
  };

  const filteredFAchat = fAchat.filter((facture) => {
    return (
      facture.clName?.toLowerCase()?.includes(searchQuery.toLowerCase()) ||
      facture.articles.some(article => article.refAr.toLowerCase().includes(searchQuery.toLowerCase()))
    );
  });

  const handleSearch = (query) => {
    setSearchQuery(query);
  };

  const handleSubmit = (newFactureData) => {
    axios.post('http://localhost:7777/factureAchat/', newFactureData)
      .then(res => {
        console.log(res);
        setModalIsOpen(false); // Close the modal after successfully creating the facture
        setFAchat([...fAchat, res.data]); 
      })
      .catch(err => console.log('Error creating facture:', err));
  };

  return (
    <div className="facture-vente-container">
      <CreateFactureVente onFactureSubmit={handleSubmit} selectedFacture={selectedFacture} />
      <button onClick={() => setModalIsOpen(true)}>Voir les factures de vente</button>
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={() => setModalIsOpen(false)}
        contentLabel="Voir les factures de vente"
        style={{
          content: {
            top: '50%',
            left: '50%',
            right: 'auto',
            bottom: 'auto',
            marginRight: '-50%',
            transform: 'translate(-50%, -50%)',
            width: '80%',
            maxWidth: '800px',
            maxHeight: '80vh',
            overflowY: 'auto',
          },
        }}
      >
        <div className="d-flex justify-content-between align-items-center mb-3">
          <h2 className="mb-4">Factures d'achat :</h2>
          <div className="input-group">
            <input
              type="text"
              className="form-control"
              placeholder="Rechercher un client ou une référence..."
              value={searchQuery}
              onChange={(e) => handleSearch(e.target.value)}
            />
            <span className="input-group-text">
              <FaSearch />
            </span>
          </div>
        </div>
        <button
        //  onClick={handleDelete}
        >Supprimer</button>
        <div className="table-responsive">
          <table className="table table-striped">
            <thead>
              <tr>
                <th>Id</th>
                <th>Code fss</th>
                <th>Nom du fss</th>
                <th>Articles</th>
                <th>Total</th>
              </tr>
            </thead>
            <tbody>
              {filteredFAchat.map((facture, index) => (
                <tr
                  key={facture.id}
                  onClick={() => {
                    console.log('Selected Facture:', facture); // Debug log
                    setSelectedFacture(facture);
                    setSelectedRow(index);
                  }}
                  className={selectedRow === index ? 'table-active' : ''}
                  style={{ cursor: 'pointer' }}
                >
                  <td>{facture.id}</td>
                  <td>{facture.client_id}</td>
                  <td>{facture.clName}</td>
                  <td>
                    {facture.articles.map((article, i) => (
                      <span key={i}>
                        {`${article.refAr}: ${article.quantité} | `}
                      </span>
                    ))}
                  </td>
                  <td>{facture.totalPrice}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </Modal>
    </div>
  );
}

export default FactureAchat;
