import React, { useState, useEffect }  from 'react'
import axios from 'axios';
import Modal from 'react-modal';
import CreateFactureAchat from './CreateFactureAchat';
import { FaSearch } from 'react-icons/fa';

Modal.setAppElement('#root');


function FactureAchat() {
  const [fAchat, setFVente] = useState([]);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [selectedFacture, setSelectedFacture] = useState(null);
  const [selectedRow, setSelectedRow] = useState(null);

  useEffect(() => {
    axios
      .get('http://localhost:7777/factureVente/')
      .then((res) => {
        const formattedFVente = res.data.map((facture) => {
          const date = new Date(facture.date);
          const formattedDate = date.toISOString().split('T')[0]; 
          return {
            ...facture,
            date: formattedDate,
          };
        });
        setFVente(formattedFVente);
      })
      .catch((err) => console.log(err));
  }, []);

  return (
    <div>aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa</div>
  )
}

export default FactureAchat
