import React, {useEffect, useState} from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';


function Article() {
    const [article,setArticle] = useState([])
    useEffect(() => {
        axios.get('http://localhost:7777/article/')
        .then(res => setArticle(res.data))
        .catch(err => console.log(err));
    }, [])

    const handleDelete = async (id) => {
        try {
            await axios.delete('http://localhost:7777/article/'+id)
            window.location.reload()
        } catch(err){
            console.log(err);
        }
    }
  return (
    <div className='d-flex vh-100 bg-primary justify-content-center align-items-center'>
        <div className='w-110 bg-white rounded p-3'>
            <Link to="/article/createAr" className='btn btn-success'>Ajouter +</Link>
            <table className='table'>
                <thead>
                    <tr>
                    <th>Id</th>
                    <th>Nom</th>
                    <th>Référence</th>
                    <th>Prix d'achat brut</th>
                    <th>Remise 1</th>
                    <th>Prix d'achat AR1</th>
                    <th>Remise 2</th>
                    <th>Prix d'achat AR2 HT</th>   
                                     
                    <th>Prix TTC</th>      
                    <th></th>                    
              
                    </tr>
                </thead>
                <tbody>
                    {
                        article.map((data,i) => (
                            <tr key={i}>
                                <td>{data.arID}</td>
                                <td>{data.arName}</td>
                                <td>{data.arPrice1}</td>
                                <td>{data.arPrice2}</td>
                                <td>{data.arTva}</td>
                                <td>
                                    <Link to={`updateAr/${data.arID}`} className='btn btn-primary'>Changer</Link>
                                    <button className='btn btn-danger ms-2' onClick={e => handleDelete(data.arID)}>Supprimer</button>
                                </td>
                            </tr>
                        ))
                    }
                </tbody>
            </table>

        </div>
    </div>
  )
}

export default Article;
