import React, { useState, useEffect } from 'react';
import axios from 'axios';

function CreateFactureVente() {
    const [client, setClient] = useState('');
    const [date, setDate] = useState('');
    const [code, setCode] = useState('');
    const [inputs, setInputs] = useState([{ refAr: '', article_quantite: '', price: 0, available_quantite: 0 }]);
    const [references, setReferences] = useState([]);
    const [telephone, setTelephone] = useState('');

    useEffect(() => {
        axios.get('http://localhost:7777/article')
            .then(res => {
                setReferences(res.data.map(article => article.ref));
            })
            .catch(err => console.log(err));
    }, []);

    const fetchArticleDetails = (reference, index) => {
        axios.get(`http://localhost:7777/article/${reference}`)
            .then(res => {
                const { price, quantite } = res.data;
                const newInputs = [...inputs];
                newInputs[index].price = price;
                newInputs[index].available_quantite = quantite;
                setInputs(newInputs);
            })
            .catch(err => console.log(err));
    };

    const handleReferenceChange = (event, index) => {
        const { value } = event.target;
        const newInputs = [...inputs];
        newInputs[index].refAr = value;
        setInputs(newInputs);
        fetchArticleDetails(value, index); // Fetch article details when reference changes
    };

    const handleQuantityChange = (event, index) => {
        const { value } = event.target;
        const newInputs = [...inputs];
        newInputs[index].article_quantite = value;
        setInputs(newInputs);
    };

    const handleAddInput = () => {
        setInputs([...inputs, { refAr: '', price: 0, article_quantite: '', available_quantite: 0 }]);
    };

    const calculateTotal = () => {
        return inputs.reduce((acc, curr) => acc + (curr.price * curr.article_quantite), 0);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
    
        // Check if any reference is invalid or quantity exceeds available quantity
        const invalidReference = inputs.some(input => !references.includes(input.refAr));
        const insufficientQuantity = inputs.some(input => parseInt(input.article_quantite, 10) > input.available_quantite);

        if (invalidReference) {
            alert("Sélectionner une référence valide");
            return; // Stop the submission process
        }

        if (insufficientQuantity) {
            const problematicInput = inputs.find(input => parseInt(input.article_quantite, 10) > input.available_quantite);
            alert(`Vous n'avez pas la quantité souhaitée pour la référence ${problematicInput.refAr}. Quantité disponible: ${problematicInput.available_quantite}`);
            return; // Stop the submission process
        }
    
        // Extract references and quantities
        const refArArray = [];
        const quantiteArray = [];
        inputs.forEach(input => {
            refArArray.push(input.refAr);
            quantiteArray.push(input.article_quantite);
        });
    
        const totalPrice = calculateTotal();
        axios.post('http://localhost:7777/factureVente/createFac/', {
            client,
            date,
            code,
            refAr: refArArray,
            article_quantite: quantiteArray,
            totalPrice
        })
            .then(res => {
                console.log(res);
                window.location.reload();
            })
            .catch(err => console.log(err))
    };

    return (
        <div className='container mt-5'>
            <h2 className="mb-4">Ajouter une facture de vente</h2>
            <form onSubmit={handleSubmit}>
                <div className="row mb-3">
                    <div className='col'>
                        <label htmlFor="">Nom du client</label>
                        <input type='text' placeholder='Entrez le nom du client' className='form-control'
                            onChange={e => setClient(e.target.value)}
                        />
                    </div>
                    <div className='col'>
                        <label htmlFor="">Date</label>
                        <input type="date" className='form-control'
                            onChange={e => setDate(e.target.value)}
                        />
                    </div>
                </div>
                <div className="row mb-3">
                    <div className='col'>
                        <label htmlFor="">Code de la facture</label>
                        <input type='text' placeholder='Entrez le code de la facture' className='form-control'
                            onChange={e => setCode(e.target.value)}
                        />
                    </div>
                    <div className='col'>
                        <label htmlFor="">Téléphone</label>
                        <input
                            type="text"
                            className='form-control'
                            placeholder="Entrez le numéro de téléphone"
                            value={telephone}
                            onChange={(e) => setTelephone(e.target.value)}
                        />
                    </div>
                </div>
                {inputs.map((input, index) => (
                    <div className="row mb-3" key={index}>
                        <div className='col'>
                            <label htmlFor="">Référence d'article</label>
                            <input
                                list="references"
                                className='form-control'
                                placeholder="Select or type reference..."
                                value={input.refAr}
                                onChange={(e) => handleReferenceChange(e, index)}
                            />
                            <datalist id="references">
                                {references.map((reference, idx) => (
                                    <option key={idx} value={reference} />
                                ))}
                            </datalist>
                        </div>
                        <div className='col'>
                            <label htmlFor="">Prix vente TTC</label>
                            <input type='number' placeholder="Prix d'article" className='form-control'
                                value={input.price} // Display the price
                                readOnly // Make it read-only
                            />
                        </div>
                        <div className='col'>
                            <label htmlFor="">Quantité</label>
                            <input type='number' placeholder='Entrez la quantité désirée' className='form-control'
                                value={input.article_quantite}
                                onChange={(e) => handleQuantityChange(e, index)}
                            />
                        </div>
                    </div>
                ))}
                <div className="row mb-3">
                    <div className='col'>
                        <label htmlFor="">Total</label>
                        <input type='number' placeholder='Total de la facture' className='form-control' value={calculateTotal()} readOnly />
                    </div>
                </div>
                <button type="button" className='btn btn-success' onClick={handleAddInput}>Ajouter</button>
                <button type="submit" className='btn btn-primary'>Enregistrer</button>
            </form>
        </div>
    );
}

export default CreateFactureVente;
