import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import CreateArticle from './CreateArticle'; // Import the CreateArticle component

function Article() {
    const [article, setArticle] = useState([]);
    useEffect(() => {
        axios.get('http://localhost:7777/article/')
            .then(res => setArticle(res.data))
            .catch(err => console.log(err));
    }, []);

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:7777/article/${id}`);
            window.location.reload();
        } catch (err) {
            console.log(err);
        }
    }

    return (
        <div className='justify-content-center align-items-center'>
            <div className='w-110 bg-white rounded p-3'>
                <Link to={`/article/createAr/`} className='btn btn-success'>Ajouter +</Link>
                <CreateArticle onAddArticle={() => window.location.reload()} />
 {/* Render the CreateArticle component */}
                <table className='table'>
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>Nom</th>
                            <th>Référence</th>
                            <th>Prix d'achat brut</th>
                            <th>Remise 1</th>
                            <th>Prix d'achat AR1</th>
                            <th>Remise 2</th>
                            <th>Prix d'achat AR2 HT</th>
                            <th>TVA</th>
                            <th>Prix TTC</th>
                            <th>Marge bénéficiaire</th>
                            <th>Prix de vente HT</th>
                            <th>Prix de vente TTC</th>
                            <th></th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            article.map((data, i) => (
                                <tr key={i}>
                                    <td>{data.arID}</td>
                                    <td>{data.arName}</td>
                                    <td>{data.ref}</td>
                                    <td>{data.prachat}</td>
                                    <td>{data.remise1}</td>
                                    <td>{data.prar1}</td>
                                    <td>{data.remise2}</td>
                                    <td>{data.prar2}</td>
                                    <td>{data.tva}%</td>
                                    <td>{data.achatttc}</td>
                                    <td>{data.margeben}</td>
                                    <td>{data.prventeht}</td>
                                    <td>{data.prventettc}</td>
                                    <td>
                                        <Link to={`updateAr/${data.arID}`} className='btn btn-primary'>Changer</Link>
                                        <button className='btn btn-danger ms-2' onClick={e => handleDelete(data.arID)}>Supprimer</button>
                                    </td>
                                </tr>
                            ))
                        }
                    </tbody>
                </table>

            </div>
        </div>
    )
}

export default Article;
