import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import CreateArticle from './CreateArticle';

function Article() {
    const [articles, setArticles] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:7777/article/')
            .then(res => setArticles(res.data))
            .catch(err => console.log(err));
    }, []);

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:7777/article/${id}`);
            setArticles(articles.filter(article => article.arID !== id));
        } catch (err) {
            console.log(err);
        }
    }

    const handleAddArticle = (newArticle) => {
        setArticles([...articles, newArticle]);
    }

    return (
        <div className='container mt-5'>
            <h2>Liste des Articles</h2>
            <Link to={`/article/createAr/`} className='btn btn-success mb-3'>Ajouter +</Link>
            <CreateArticle onAddArticle={handleAddArticle} />
            <div className='table-responsive'>
                <table className='table table-striped'>
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>Nom</th>
                            <th>Référence</th>
                            <th>Prix d'achat brut</th>
                            <th>Remise 1</th>
                            <th>Prix d'achat AR1</th>
                            <th>Remise 2</th>
                            <th>Prix d'achat AR2 HT</th>
                            <th>TVA</th>
                            <th>Prix TTC</th>
                            <th>Marge bénéficiaire</th>
                            <th>Prix de vente HT</th>
                            <th>Prix de vente TTC</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {articles.map(article => (
                            <tr key={article.arID} style={{ height: '50px' }}>
                                <td>{article.arID}</td>
                                <td>{article.arName}</td>
                                <td>{article.ref}</td>
                                <td>{article.prachat}</td>
                                <td>{article.remise1}</td>
                                <td>{article.prar1}</td>
                                <td>{article.remise2}</td>
                                <td>{article.prar2}</td>
                                <td>{article.tva}%</td>
                                <td>{article.achatttc}</td>
                                <td>{article.margeben}</td>
                                <td>{article.prventeht}</td>
                                <td>{article.prventettc}</td>
                                <td>
                                    <Link to={`updateAr/${article.arID}`} className='btn btn-primary'>Modifier</Link>
                                    <button className='btn btn-danger ms-2' onClick={() => handleDelete(article.arID)}>Supprimer</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default Article;
