import axios from 'axios';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';


function CreateArticle() {
    const [name,setName] = useState('')
    const [ref,setRef] = useState('')
    const [achatbrut,setAchatbrut] = useState('')
    const [remise1,setRemise1] = useState('')
    const [achatar1,setAchatar1] = useState('')   
    const [remise2,setRemise2] = useState('')
    const [achatar2,setAchatar2] = useState('')
    const [tva,setTva] = useState('')
    const [achatttc,setAchatttc] = useState('')
    const [mbenif,setMbenif] = useState('')
    const [venteht,setVenteht] = useState('')
    const [ventettc,setVentettc] = useState('')
    
    const navigate = useNavigate();


function handleSubmit(event) {
    event.preventDefault();
    axios.post('http://localhost:7777/article/createAr/',{
    arName: name,
    ref: ref,
    prachat: achatbrut,
    tva: tva
})
    .then(res => {
        console.log(res);
        navigate('/article/');

    }).catch(err => console.log(err))
} 
    
  return (
    <div className='d-flex vh-100 bg-primary justify-content-center align-items-center'>
        <div className='w-50 bg-white rounded p-3'>
            <form onSubmit={handleSubmit}>
                <h2>Ajouter un article</h2>
                <div className='mb-2'>
                    <label htmlFor="">Nom</label>
                    <input type='text' placeholder="entrez le nom d'article" className='form-control'
                    onChange={e => setName(e.target.value)}
                    ></input>
                </div>
                <div className='mb-2'>
                    <label htmlFor="">1er Prix</label>
                    <input type='text' placeholder="entrez le premier prix de cet article" className='form-control'
                    onChange={e => setRef(e.target.value)}
                    ></input>
                </div>
                <div className='mb-2'>
                    <label htmlFor="">2ème Prix</label>
                    <input type='text' placeholder="entrez le deuxième prix de cet article" className='form-control'
                    onChange={e => setAchatbrut(e.target.value)}
                    ></input>
                </div>
                <div className='mb-2'>
                    <label htmlFor="">TVA</label>
                    <input type='text' placeholder='entrez le TVA de cet article' className='form-control'
                    onChange={e => setTva(e.target.value)}
                    ></input>
                </div>
                <button className='btn btn-success'>Ajouter</button>
            </form>
        </div>
    </div>
  )
}

export default CreateArticle;
