import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import CreateArticle from './CreateArticle';

function Article() {
    const [articles, setArticles] = useState([]);
    const [showAdditionalColumns, setShowAdditionalColumns] = useState(false);

    useEffect(() => {
        axios.get('http://localhost:7777/article/')
            .then(res => setArticles(res.data))
            .catch(err => console.log(err));
    }, []);

    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:7777/article/${id}`);
            setArticles(articles.filter(article => article.arID !== id));
        } catch (err) {
            console.log(err);
        }
    }

    const handleAddArticle = (newArticle) => {
        setArticles([...articles, newArticle]);
    }

    const toggleAdditionalColumns = () => {
        setShowAdditionalColumns(!showAdditionalColumns);
    }

    return (
        <div className='article-container'>
            <CreateArticle onAddArticle={handleAddArticle} /><br />
            <h2>Liste des Articles</h2>
            <div className='table-responsive'>
                <table className='table table-striped'>
                    <thead>
                        <tr>
                            <th>Id</th> 
                            <th>Nom</th>
                            <th>Référence</th>
                            <th>Achat brut</th>
                            <th>Remise1</th>
                            {showAdditionalColumns && (
                                <>
                                    <th>Prix d'achat AR1</th>
                                    <th>Remise2</th>
                                    <th>Achat AR2 HT</th>
                                    <th>TVA</th>
                                    <th>Prix TTC</th>
                                    <th>Marge bénéficiaire</th>
                                    <th>Vente HT</th>
                                    <th>Vente TTC</th>
                                </>
                            )}
                            <th>Modifier</th>
                            <th>Supprimer</th>
                            <th>
                                <button className='btn btn-secondary' onClick={toggleAdditionalColumns}>
                                    {showAdditionalColumns ? 'Cacher colonnes supplémentaires' : 'Afficher colonnes supplémentaires'}
                                </button>
                            </th>
                        </tr>
                    </thead>
                    <tbody>
                        {articles.map(article => (
                            <tr key={article.arID}>
                                <td>{article.arID}</td>
                                <td>{article.arName}</td>
                                <td>{article.ref}</td>
                                <td>{article.prachat}</td>
                                <td>{article.remise1}</td>
                                {showAdditionalColumns && (
                                    <>
                                        <td>{article.prar1}</td>
                                        <td>{article.remise2}</td>
                                        <td>{article.prar2}</td>
                                        <td>{article.tva}%</td>
                                        <td>{article.achatc}</td>
                                        <td>{article.margeben}</td>
                                        <td>{article.prventeht}</td>
                                        <td>{article.prventettc}</td>
                                    </>
                                )}
                                <td>
                                    <Link to={`updateAr/${article.arID}`} className='btn btn-primary'>Modifier</Link>
                                </td>
                                <td>
                                    <button className='btn btn-danger ms-2' onClick={() => handleDelete(article.arID)}>Supprimer</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default Article;
