import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function CreateArticle() {
  const [name, setName] = useState("");
  const [ref, setRef] = useState("");
  const [achatbrut, setAchatbrut] = useState("");
  const [remise1, setRemise1] = useState("");
  const [remise2, setRemise2] = useState("0");
  const [achatttc, setAchatttc] = useState("");
  const [achatc, setAchatc] = useState("");
  const [mbenif, setMbenif] = useState("");
  const [venteht, setVenteht] = useState("");
  const [ventettc, setVentettc] = useState("");
  const [autofillar1, setAutofillar1] = useState("");
  const [autofillar2, setAutofillar2] = useState("0");
  const [selectedTva, setSelectedTva] = useState("0");

  const navigate = useNavigate();

  const handleSubmit = (event) => {
    event.preventDefault();
    if (selectedTva !== null && selectedTva !== undefined) {
      axios
        .post("http://localhost:7777/article/createAr/", {
          arName: name,
          ref: ref,
          prachat: achatbrut,
          remise1: remise1,
          prar1: autofillar1,
          remise2: remise2,
          prar2: autofillar2,
          tva: parseInt(selectedTva),
          achatttc: achatttc,
          achatc: achatc,
          margeben: mbenif,
          prventeht: venteht,
          prventettc: ventettc,
        })
        .then((res) => {
          console.log(achatc);
          // window.location.reload();
        })
        .catch((err) => console.log(err));
    } else {
      console.log("Invalid value of tva");
    }
  };

  useEffect(() => {
    // Calculate Prix d'achat AR1
    if (achatbrut && remise1) {
      const prixAR1 = parseFloat(achatbrut) * (1 - parseFloat(remise1) / 100);
      setAutofillar1(prixAR1.toFixed(2));
    } else {
      setAutofillar1("");
    }
  }, [achatbrut, remise1]);

  useEffect(() => {
    // Calculate Prix d'achat AR2
    if (autofillar1 && remise2) {
      const prixAR2 = parseFloat(autofillar1) * (1 - parseFloat(remise2) / 100);
      setAutofillar2(prixAR2.toFixed(2));
    } else {
      setAutofillar2("");
    }
  }, [autofillar1, remise2]);

  useEffect(() => {
    if (autofillar1 && remise2) {
      // If autofillar1 and remise2 have values, set achatttc to autofillar2
      setAchatttc(autofillar2);
    } else {
      // Otherwise, set achatttc to autofillar1
      setAchatttc(autofillar1);
    }
  }, [autofillar1, autofillar2, remise2]);

  useEffect(() => {
    // Check if both achatttc and mbenif have values
    if (achatttc && mbenif) {
      // Calculate the sum of achatttc and mbenif
      const sum = (parseFloat(achatttc) / 100 * parseFloat(mbenif)) + parseFloat(achatttc);
      // Set the sum as the value of venteht
      setVenteht(sum.toString());
    } else {
      // Clear venteht if any of the inputs are empty
      setVenteht("");
    }
  }, [achatttc, mbenif]);

  useEffect(() => {
    if (venteht && selectedTva) {
      const sum = parseFloat(venteht) + (parseFloat(venteht) * parseFloat(selectedTva) / 100);
      setVentettc(sum.toFixed(2));
    } else {
      setVentettc("");
    }
  }, [venteht, selectedTva]);

  useEffect(() => {
    if (achatttc && selectedTva) {
      const sum = parseFloat(achatttc) + (parseFloat(achatttc) * parseFloat(selectedTva) / 100);
      setAchatc(sum.toFixed(2));
    } else {
      setAchatc("");
    }
  }, [achatttc, selectedTva]);

  return (
    <div style={{ margin: "20px", display: "flex", justifyContent: "flex-end" }}>
      {/* Column 1 */}
      <div style={{ flex: "0.7", marginRight: "10px" }}>
        <div style={{ marginBottom: "10px" }}>
          <label style={{ width: "120px" }} htmlFor="name">Nom d'article</label>
          <input
            style={{ width: "200px" }}
            type="text"
            id="name"
            placeholder="Entrez le nom d'article"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label style={{ width: "120px" }} htmlFor="ref">Référence</label>
          <input
            style={{ width: "200px" }}
            type="text"
            id="ref"
            placeholder="Entrez la référence de cet article"
            value={ref}
            onChange={(e) => setRef(e.target.value)}
          />
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label style={{ width: "120px" }} htmlFor="achatbrut">Prix d'achat brut</label>
          <input
            style={{ width: "200px" }}
            type="number"
            id="achatbrut"
            placeholder="Entrez le prix d'achat brut de cet article"
            value={achatbrut}
            onChange={(e) => setAchatbrut(e.target.value)}
          />
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label style={{ width: "120px" }} htmlFor="remise1">Remise 1</label>
          <input
            style={{ width: "200px" }}
            type="number"
            id="remise1"
            placeholder="Entrez la 1ère remise de cet article"
            value={remise1}
            onChange={(e) => setRemise1(e.target.value)}
          />
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label style={{ width: "120px" }} htmlFor="autofillar1">Prix d'achat AR1</label>
          <input style={{ width: "200px" }} type="text" id="autofillar1" readOnly value={autofillar1} />
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label style={{ width: "120px" }} htmlFor="remise2">Remise 2</label>
          <input
            style={{ width: "200px" }}
            type="text"
            id="remise2"
            placeholder="Entrez la 2ème remise de cet article"
            value={remise2}
            onChange={(e) => setRemise2(e.target.value)}
          />
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label style={{ width: "120px" }} htmlFor="autofillar2">Prix d'achat AR2</label>
          <input style={{ width: "200px" }} type="text" id="autofillar2" readOnly value={autofillar2} />
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label style={{ width: "120px" }} htmlFor="achatttc">Prix d'achat HT</label>
          <input
            style={{ width: "200px" }}
            type="text"
            id="achatttc"
            placeholder="Entrez le HT de cet article"
            value={achatttc}
            onChange={(e) => setAchatttc(e.target.value)}
          />
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label style={{ width: "120px" }} htmlFor="tva">TVA</label>
          <select
            style={{ width: "200px" }}
            id="tva"
            value={selectedTva}
            onChange={(e) => setSelectedTva(e.target.value)}
          >
            <option value="0">0%</option>
            <option value="7">7%</option>
            <option value="13">13%</option>
            <option value="19">19%</option>
          </select>
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label style={{ width: "120px" }} htmlFor="achatc">Prix d'achatc TTC</label>
          <input style={{ width: "200px" }} type="text" id="achatc" readOnly value={achatc} />
        </div>
      </div>

      {/* Column 2 */}
      <div style={{ flex: "", marginRight: "10px" }}>
        <div style={{ marginBottom: "10px" }}>
          <label style={{ width: "120px" }} htmlFor="mbenif">Marge bénéficiaire</label>
          <input
            style={{ width: "200px" }}
            type="text"
            id="mbenif"
            placeholder="Entrez la marge bénéficiaire désirée"
            value={mbenif}
            onChange={(e) => setMbenif(e.target.value)}
          />
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label style={{ width: "120px" }} htmlFor="venteht">Prix de vente HT</label>
          <input style={{ width: "200px" }} type="text" id="venteht" readOnly value={venteht} />
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label style={{ width: "120px" }} htmlFor="ventettc">Prix de vente TTC</label>
          <input style={{ width: "200px" }} type="text" id="ventettc" readOnly value={ventettc} />
        </div>
      </div>

      {/* Column 3 */}
      <div style={{ flex: "1", display: "flex", flexDirection: "column", alignItems: "center" }}>
        <button style={{ width: "200px", marginBottom: "10px" }} type="submit">Ajouter</button>
        <button style={{ width: "200px", marginBottom: "10px" }} type="button">Supprimer</button>
        <button style={{ width: "200px" }} type="button">Enregistrer</button>
      </div>
    </div>
  );
}

export default CreateArticle;
