import React, { useState, useEffect } from 'react';
import axios from 'axios';

function CreateFactureVente({ onAddFVente, onDelete, selectedFacture }) {
    const [client, setClient] = useState('');
    const [date, setDate] = useState('');
    const [code, setCode] = useState('');
    const [inputs, setInputs] = useState([{ refAr: '', quantité: '', price: 0 }]);
    const [references, setReferences] = useState([]);
    const [telephone, setTelephone] = useState('');

    useEffect(() => {
        axios.get('http://localhost:7777/article')
            .then(res => setReferences(res.data.map(article => article.ref)))
            .catch(err => console.log(err));
    }, []);

    useEffect(() => {
        if (selectedFacture) {
            setClient(selectedFacture.client || '');
            setDate(selectedFacture.date || '');
            setCode(selectedFacture.code || '');
            setInputs(selectedFacture.inputs || [{ refAr: '', quantité: '', price: 0 }]);
            setTelephone(selectedFacture.telephone || '');
        }
    }, [selectedFacture]);

    const fetchPriceByReference = (reference, index) => {
        axios.get(`http://localhost:7777/factureVente/${reference}`)
            .then(res => {
                const price = res.data.price;
                const newInputs = [...inputs];
                newInputs[index].price = price;
                setInputs(newInputs);
            })
            .catch(err => console.log(err));
    };

    const handleReferenceChange = (event, index) => {
        const { value } = event.target;
        const newInputs = [...inputs];
        newInputs[index].refAr = value;
        setInputs(newInputs);
        fetchPriceByReference(value, index);
    };

    const handleQuantityChange = (event, index) => {
        const { value } = event.target;
        const newInputs = [...inputs];

        axios.get(`http://localhost:7777/article/${newInputs[index].refAr}/quantity`)
            .then(res => {
                const availableQuantity = res.data.quantity;
                const remainingQuantity = availableQuantity - parseInt(value);

                if (remainingQuantity < 0) {
                    alert(`La quantité souhaitée n'est pas suffisante, vous n'avez que ${availableQuantity} pièces de cet article`);
                    newInputs[index].quantité = availableQuantity;
                } else {
                    newInputs[index].quantité = value;
                }
                setInputs(newInputs);
            })
            .catch(err => console.error(err));
    };

    const handleAddInput = () => {
        setInputs([...inputs, { refAr: '', price: 0, quantité: '' }]);
    };

    const calculateTotal = () => {
        return inputs.reduce((acc, curr) => acc + (curr.price * curr.quantité), 0);
    };

    const handleSubmit = (event) => {
        event.preventDefault();

        const invalidReference = inputs.some(input => !references.includes(input.refAr));
        if (invalidReference) {
            alert("Sélectionner une référence valide");
            return;
        }

        const refArArray = [];
        const quantiteArray = [];
        inputs.forEach(input => {
            refArArray.push(input.refAr);
            quantiteArray.push(input.quantité);
        });

        const totalPrice = calculateTotal();
        axios.post('http://localhost:7777/factureVente/createFac/', {
            client,
            date,
            code,
            refAr: refArArray,
            quantité: quantiteArray,
            totalPrice
        })
            .then(res => {
                onAddFVente({
                    client,
                    date,
                    code,
                    refAr: refArArray,
                    quantité: quantiteArray,
                    totalPrice
                });
                updateArticleQuantities(refArArray, quantiteArray);
                clearInputs();
            })
            .catch(err => console.log(err));
    };

    const updateArticleQuantities = (refArArray, quantiteArray) => {
        refArArray.forEach((refAr, index) => {
            const updatedQuantity = parseInt(quantiteArray[index]);
            axios.put(`http://localhost:7777/article/${refAr}/updateQuantity`, {
                updatedQuantity
            })
            .then(res => console.log(res))
            .catch(err => console.error(err));
        });
    };

    const clearInputs = () => {
        setClient('');
        setDate('');
        setCode('');
        setInputs([{ refAr: '', quantité: '', price: 0 }]);
        setTelephone('');
    };

    return (
        <div className="container mt-5">
            <h2 className="mb-4">Ajouter une facture de vente</h2>
            <form onSubmit={handleSubmit}>
                <div className="row mb-3">
                    <div className='col-md-6'>
                        <label htmlFor="client">Nom du client</label>
                        <input 
                            type='text' 
                            id="client" 
                            placeholder='Entrez le nom du client' 
                            className='form-control'
                            value={client}
                            onChange={e => setClient(e.target.value)}
                        />
                    </div>
                    <div className='col-md-6'>
                        <label htmlFor="date">Date</label>
                        <input 
                            type="date" 
                            id="date" 
                            className='form-control'
                            value={date}
                            onChange={e => setDate(e.target.value)}
                        />
                    </div>
                </div>
                <div className="row mb-3">
                    <div className='col-md-6'>
                        <label htmlFor="code">Code de la facture</label>
                        <input 
                            type='text' 
                            id="code" 
                            placeholder='Entrez le code de la facture' 
                            className='form-control'
                            value={code}
                            onChange={e => setCode(e.target.value)}
                        />
                    </div>
                    <div className='col-md-6'>
                        <label htmlFor="telephone">Téléphone</label>
                        <input
                            type="text"
                            id="telephone"
                            className='form-control'
                            placeholder="Entrez le numéro de téléphone"
                            value={telephone}
                            onChange={(e) => setTelephone(e.target.value)}
                        />
                    </div>
                </div>
                {inputs.map((input, index) => (
                    <div className="row mb-3" key={index}>
                        <div className='col-md-4'>
                            <label htmlFor={`refAr-${index}`}>Référence d'article</label>
                            <input
                                list="references"
                                id={`refAr-${index}`}
                                className='form-control'
                                placeholder="Sélectionnez ou tapez la référence..."
                                value={input.refAr}
                                onChange={(e) => handleReferenceChange(e, index)}
                            />
                            <datalist id="references">
                                {references.map((reference, idx) => (
                                    <option key={idx} value={reference} />
                                ))}
                            </datalist>
                        </div>
                        <div className='col-md-4'>
                            <label htmlFor={`price-${index}`}>Prix vente TTC</label>
                            <input 
                                type='number' 
                                id={`price-${index}`}
                                placeholder="Prix d'article" 
                                className='form-control'
                                value={input.price} 
                                readOnly 
                            />
                        </div>
                        <div className='col-md-4'>
                            <label htmlFor={`quantité-${index}`}>Quantité</label>
                            <input 
                                type='number' 
                                id={`quantité-${index}`}
                                placeholder='Entrez la quantité désirée' 
                                className='form-control'
                                value={input.quantité}
                                onChange={(e) => handleQuantityChange(e, index)}
                            />
                        </div>
                    </div>
                ))}
                <div className="row mb-3">
                    <div className='col-md-12'>
                        <label htmlFor="total">Total</label>
                        <input 
                            type='number' 
                            id="total"
                            placeholder='Total de la facture' 
                            className='form-control' 
                            value={calculateTotal()} 
                            readOnly 
                        />
                    </div>
                </div>
                <div className="d-flex justify-content-between">
                    <button type="button" className='btn btn-success' onClick={handleAddInput}>Ajouter</button>
                    <button type="submit" className='btn btn-primary'>Enregistrer</button>
                    <button type="button" className='btn btn-secondary' onClick={clearInputs}>Vider les champs</button>
                </div>
            </form>
        </div>
    );
}

export default CreateFactureVente;
