import React, { useState, useEffect } from 'react';
import axios from 'axios';

function CreateFactureVente() {
    const [client, setClient] = useState('');
    const [date, setDate] = useState('');
    const [code, setCode] = useState('');
    const [inputs, setInputs] = useState([{ refAr: '', quantité: '', price: 0 }]);
    const [references, setReferences] = useState([]);
    const [telephone, setTelephone] = useState('');

    useEffect(() => {
        axios.get('http://localhost:7777/article')
            .then(res => {
                setReferences(res.data.map(article => article.ref));
            })
            .catch(err => console.log(err));
    }, []);

    const fetchPriceByReference = (reference, index) => {
        axios.get(`http://localhost:7777/factureVente/${reference}`) 
            .then(res => {
                const price = res.data.price;
                const newInputs = [...inputs];
                newInputs[index].price = price;
                setInputs(newInputs);
            })
            .catch(err => console.log(err));
    };
    
    const handleReferenceChange = (event, index) => {
        const { value } = event.target;
        const newInputs = [...inputs];
        newInputs[index].refAr = value;
        setInputs(newInputs);
        fetchPriceByReference(value, index); // Fetch price when reference changes
    };

    const handleQuantityChange = (event, index) => {
        const { value } = event.target;
        const newInputs = [...inputs];
        newInputs[index].quantité = value;
        setInputs(newInputs);
    };

    const handleAddInput = () => {
        setInputs([...inputs, { refAr: '', price: 0, quantité: '' }]);
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        const totalPrice = inputs.reduce((acc, curr) => acc + (curr.quantité * curr.price), 0);
        axios.post('http://localhost:7777/factureVente/createFac/', {
            client,
            date,
            code,
            refAr: inputs.map(input => input.refAr),
            quantité: inputs.map(input => input.quantité),
            totalPrice
        })
            .then(res => {
                console.log(res);
                window.location.reload();
            })
            .catch(err => console.log(err))
    };

    return (
        <div className='container mt-5'>
            <h2 className="mb-4">Ajouter une facture de vente</h2>
            <form onSubmit={handleSubmit}>
                <div className="row mb-3">
                    <div className='col'>
                        <label htmlFor="">Nom du client</label>
                        <input type='text' placeholder='Entrez le nom du client' className='form-control'
                            onChange={e => setClient(e.target.value)}
                        />
                    </div>
                    <div className='col'>
                        <label htmlFor="">Date</label>
                        <input type="date" className='form-control'
                            onChange={e => setDate(e.target.value)}
                        />
                    </div>
                </div>
                <div className="row mb-3">
                    <div className='col'>
                        <label htmlFor="">Code de la facture</label>
                        <input type='text' placeholder='Entrez le code de la facture' className='form-control'
                            onChange={e => setCode(e.target.value)}
                        />
                    </div>
                    <div className='col'>
                        <label htmlFor="">Téléphone</label>
                        <input
                            type="text"
                            className='form-control'
                            placeholder="Entrez le numéro de téléphone"
                            value={telephone}
                            onChange={(e) => setTelephone(e.target.value)}
                        />
                    </div>
                </div>
                {inputs.map((input, index) => (
                    <div className="row mb-3" key={index}>
                        <div className='col'>
                            <label htmlFor="">Référence d'article</label>
                            <input
                                list="references"
                                className='form-control'
                                placeholder="Select or type reference..."
                                value={input.refAr}
                                onChange={(e) => handleReferenceChange(e, index)}
                            />
                            <datalist id="references">
                                {references.map((reference, idx) => (
                                    <option key={idx} value={reference} />
                                ))}
                            </datalist>
                        </div>
                        <div className='col'>
                            <label htmlFor="">Prix Article</label>
                            <input type='number' placeholder="Prix d'article" className='form-control'
                                value={input.price} // Display the price
                                readOnly // Make it read-only
                            />
                        </div>
                        <div className='col'>
                            <label htmlFor="">Quantité</label>
                            <input type='number' placeholder='Entrez la quantité désirée' className='form-control'
                                value={input.quantité}
                                onChange={(e) => handleQuantityChange(e, index)}
                            />
                        </div>
                    </div>
                ))}
                <div className="row mb-3">
                    <div className='col'>
                        <label htmlFor="">Total</label>
                        <input type='number' placeholder='Total de la facture' className='form-control' />
                    </div>
                </div>
                <button type="button" className='btn btn-success' onClick={handleAddInput}>Ajouter</button>
                <button type="submit" className='btn btn-primary'>Enregistrer</button>
            </form>
        </div>
    );
}

export default CreateFactureVente;
