import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { FaSearch } from "react-icons/fa"; // Importing the search icon

const Facture = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const [name, setName] = useState("");
  const [address, setAddress] = useState("");
  const [tel, setTel] = useState("");
  const [mf, setMF] = useState("");
  const [rc, setRC] = useState("");
  const [res, setRes] = useState("");
  const [resTel, setResTel] = useState("");
  const navigate = useNavigate();

  const handleSubmit = (event) => {
    event.preventDefault();
    axios
      .post("http://localhost:7777/client/createClient/", {
        nom: name,
        adresse: address,
        telephone: tel,
        matriculeFiscale: mf,
        registreCommercial: rc,
        nomResponsable: res,
        telephoneResponsable: resTel,
      })
      .then((res) => {
        console.log(res);
        navigate("/client");
      })
      .catch((err) => console.log(err));
  };

  const handleSearch = (query) => {
    // Add your search logic here
    console.log("Searching for:", query);
    setSearchQuery(query);
  };

  return (
    <div className="container mt-5">
      <h2 className="mb-4">Ajouter un client</h2>
      <form onSubmit={handleSubmit}>
        <div className="input-group mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="Rechercher un client..."
            value={searchQuery}
            onChange={(e) => handleSearch(e.target.value)}
          />
          <button type="submit" className="btn btn-outline-secondary">
            <FaSearch />
          </button>
        </div>
        <div className="row mb-3">
          <div className="col">
            <label htmlFor="">Nom</label>
            <input
              type="text"
              placeholder="entrez le nom du client"
              className="form-control"
              onChange={(e) => setName(e.target.value)}
            ></input>
          </div>
          <div className="col">
            <label htmlFor="">Adresse</label>
            <input
              type="text"
              placeholder="entrez l'adresse du client"
              className="form-control"
              onChange={(e) => setAddress(e.target.value)}
            ></input>
          </div>
        </div>
        <div className="row mb-3">
          <div className="col">
            <label htmlFor="">Telephone</label>
            <input
              type="number"
              placeholder="entrez le telephone du client"
              className="form-control"
              onChange={(e) => setTel(e.target.value)}
            ></input>
          </div>
          <div className="col">
            <label htmlFor="">Matricule Fiscale</label>
            <input
              type="text"
              placeholder="entrez le MF du client"
              className="form-control"
              onChange={(e) => setMF(e.target.value)}
            ></input>
          </div>
        </div>
        <div className="row mb-3">
          <div className="col">
            <label htmlFor="">Registre Commerciale</label>
            <input
              type="text"
              placeholder="entrez le RC du client"
              className="form-control"
              onChange={(e) => setRC(e.target.value)}
            ></input>
          </div>
          <div className="col">
            <label htmlFor="">Nom du responsable</label>
            <input
              type="text"
              placeholder="entrez le nom du responsable"
              className="form-control"
              onChange={(e) => setRes(e.target.value)}
            ></input>
          </div>
        </div>
        <div className="row mb-3">
          <div className="col">
            <label htmlFor="">Telephone du responsable</label>
            <input
              type="number"
              placeholder="entrez le telephone du responsable"
              className="form-control"
              onChange={(e) => setResTel(e.target.value)}
            ></input>
          </div>
        </div>
        <button type="submit" className="btn btn-success">Ajouter</button>
      </form>
    </div>
  );
};

export default Facture;
