import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';

function CreateFactureVente({ selectedFacture }) {
  const [clName, setClName] = useState('');
  const [client_id, setClientCode] = useState('');
  const [date, setDate] = useState('');
  const [DT, setDT] = useState('1.000');
  const [inputs, setInputs] = useState([{ refAr: '', quantité: '', price: 0, remise: 0 }]);
  const [references, setReferences] = useState([]);
  const [filteredClients, setFilteredClients] = useState([]);

  const remiseInputRef = useRef(null);

  useEffect(() => {
    axios.get('http://localhost:7777/article')
      .then(res => {
        setReferences(res.data.map(article => article.ref));
      })
      .catch(err => console.log('Error fetching articles:', err));
  }, []);

  useEffect(() => {
    if (selectedFacture) {
      console.log('Selected Facture in CreateFactureVente:', selectedFacture);
      setClName(selectedFacture.clName || '');
      setClientCode(selectedFacture.client_id || '');
      setDate(selectedFacture.date || '');
      setDT(selectedFacture.DT || '1.000');
      setInputs(selectedFacture.articles || [{ refAr: '', quantité: '', price: 0, remise: 0 }]);
    }
  }, [selectedFacture]);

  const fetchPriceByReference = (reference, index) => {
    axios.get(`http://localhost:7777/factureVente/${reference}`)
      .then(res => {
        const price = res.data.price;
        const newInputs = [...inputs];
        newInputs[index].price = price;
        setInputs(newInputs);
      })
      .catch(err => console.log('Error fetching price:', err));
  };

  const handleReferenceChange = (event, index) => {
    const { value } = event.target;
    const newInputs = [...inputs];
    newInputs[index].refAr = value;
    setInputs(newInputs);
    fetchPriceByReference(value, index);
  };

  const handleRemiseChange = (event, index) => {
    const { value } = event.target;
    const newInputs = [...inputs];
    newInputs[index].remise = value;
    setInputs(newInputs);
  };

  const handleQuantityChange = (event, index) => {
    const { value } = event.target;
    const newInputs = [...inputs];
    newInputs[index].quantité = value;

    axios.get(`http://localhost:7777/article/${newInputs[index].refAr}/quantity`)
      .then(res => {
        const availableQuantity = res.data.quantity;
        const remainingQuantity = availableQuantity - parseInt(value);

        if (remainingQuantity < 0) {
          alert(`La quantité souhaitée n'est pas suffisante, vous n'avez que ${availableQuantity} pièces de cet article`);
          newInputs[index].quantité = availableQuantity;
        }

        setInputs(newInputs);
      })
      .catch(err => console.error('Error fetching quantity:', err));
  };

  const handleAddInput = () => {
    const isEmpty = inputs.some(input => input.refAr === '' || input.quantité === '' || input.remise === '');
    if (isEmpty) {
      alert("Veuillez remplir tous les champs de la ligne précédente avant d'ajouter une nouvelle ligne.");
      return;
    }
    setInputs([...inputs, { refAr: '', price: 0, quantité: '', remise: 0 }]);
  };

  const calculateTotal = () => {
    if (!inputs) return 0;
    const total = inputs.reduce((acc, curr) => acc + (curr.price * curr.quantité) * (1 - (curr.remise / 100)), 0) + parseFloat(DT);
    return total.toFixed(3);
  };

  const updateStock = (inputs) => {
    inputs.forEach(input => {
      axios.put(`http://localhost:7777/article/${input.refAr}/updateStock`, { quantity: -input.quantité })
        .then(res => {
          console.log('Stock updated:', res);
        })
        .catch(err => console.error('Error updating stock:', err));
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    const nonEmptyInputs = inputs.filter(input => input.refAr !== '' && input.quantité !== '' && input.remise !== '');
    const invalidReference = nonEmptyInputs.some(input => !references.includes(input.refAr));

    if (invalidReference) {
      alert("Sélectionner une référence valide");
      return;
    }

    const refArArray = nonEmptyInputs.map(input => ({
      refAr: input.refAr,
      price: input.price,
      quantité: input.quantité,
      remise: input.remise
    }));

    const totalPrice = calculateTotal();
    axios.post('http://localhost:7777/factureVente/createFac/', {
      clName,
      client_id,
      date,
      DT,
      refAr: refArArray,
      totalPrice
    })
    .then(res => {
      console.log(res);
      updateStock(refArArray); // Update stock after successful creation
      window.location.reload();
    })
    .catch(err => console.log('Error creating facture:', err));
  };

  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
      handleAddInput();
    }
  };

  const handleClientSelect = (name, code) => {
    setClName(name);
    setClientCode(code);
    setFilteredClients([]);
  };

  const handleClientChange = (e) => {
    const { value } = e.target;
    setClName(value);

    axios.get(`http://localhost:7777/clients/search?name=${value}`)
      .then(res => {
        setFilteredClients(res.data);
      })
      .catch(err => console.log('Error fetching clients:', err));

    axios.get(`http://localhost:7777/clients/search?name=${value}`)
      .then(res => {
        if (res.data.length > 0) {
          setClientCode(res.data[0].clID);
        } else {
          setClientCode('');
        }
      })
      .catch(err => console.log('Error fetching client code:', err));
  };

  const handleClientBlur = () => {
    setFilteredClients([]);
  };

  const handleClientCodeChange = (e) => {
    const { value } = e.target;
    setClientCode(value);

    axios.get(`http://localhost:7777/clients/search?id=${value}`)
      .then(res => {
        if (res.data.length > 0) {
          setClName(res.data[0].clName);
        } else {
          setClName('');
        }
      })
      .catch(err => console.log('Error fetching client name:', err));
  };

  const handleUpdate = (event) => {
    event.preventDefault();

    const nonEmptyInputs = inputs.filter(input => input.refAr !== '' && input.quantité !== '' && input.remise !== '');
    const invalidReference = nonEmptyInputs.some(input => !references.includes(input.refAr));

    if (invalidReference) {
      alert("Sélectionner une référence valide");
      return;
    }

    const refArArray = nonEmptyInputs.map(input => ({
      refAr: input.refAr,
      price: input.price,
      quantité: input.quantité,
      remise: input.remise
    }));

    const totalPrice = calculateTotal();
    axios.put(`http://localhost:7777/factureVente/update/${selectedFacture.id}`, {
      clName,
      client_id,
      date,
      DT,
      articles: refArArray,
      totalPrice
    })
    .then(res => {
      console.log(res);
      updateStock(refArArray); // Update stock after successful update
      window.location.reload();
    })
    .catch(err => console.log('Error updating facture:', err));
  };

  return (
    <div style={{ margin: '20px', justifyContent: 'center' }}>
      <div style={{ display: 'flex', flex: '1' }}>
        <div>
          <div style={{ marginBottom: '20px' }}>
            <label style={{ width: '120px' }} htmlFor="code-client">Code client:</label>
            <input
              style={{ width: '200px' }}
              type="text"
              id="code-client"
              name="code-client"
              value={client_id}
              onChange={handleClientCodeChange}
            />
          </div>
          <div style={{ marginBottom: '20px', position: 'relative' }}>
            <label style={{ width: '120px' }} htmlFor="client">Nom du client</label>
            <input
              style={{ width: '200px' }}
              type="text"
              id="client"
              value={clName}
              onChange={handleClientChange}
              onBlur={handleClientBlur}
            />
            {filteredClients.length > 0 && (
              <ul style={{
                position: 'absolute', zIndex: 1, backgroundColor: 'white', border: '1px solid #ccc',
                listStyleType: 'none', padding: '0', margin: '0', width: '200px', maxHeight: '100px', overflowY: 'auto'
              }}>
                {filteredClients.map((client) => (
                  <li key={client.clID} style={{ padding: '8px', cursor: 'pointer' }} onClick={() => handleClientSelect(client.clName, client.clID)}>
                    {client.clName}
                  </li>
                ))}
              </ul>
            )}
          </div>
          <div style={{ marginBottom: '20px' }}>
            <label style={{ width: '120px' }} htmlFor="date">Date:</label>
            <input
              style={{ width: '200px' }}
              type="date"
              id="date"
              name="date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
            />
          </div>
        </div>
        <div style={{ marginLeft: '20px' }}>
          <div style={{ marginBottom: '20px' }}>
            <label style={{ width: '120px' }} htmlFor="DT">DT:</label>
            <input
              style={{ width: '200px' }}
              type="number"
              step="0.001"
              id="DT"
              name="DT"
              value={DT}
              onChange={(e) => setDT(e.target.value)}
            />
          </div>
          <div style={{ marginBottom: '20px' }}>
            <label style={{ width: '120px' }}>Montant Total:</label>
            <input
              style={{ width: '200px' }}
              type="text"
              readOnly
              value={calculateTotal()}
            />
          </div>
        </div>
      </div>
      <form onSubmit={selectedFacture ? handleUpdate : handleSubmit}>
        {inputs.map((input, index) => (
          <div key={index} style={{ marginBottom: '10px', display: 'flex', alignItems: 'center' }}>
            <div style={{ marginRight: '10px' }}>
              <label htmlFor={`reference-${index}`}>Référence:</label>
              <input
                list="reference-list"
                id={`reference-${index}`}
                name={`reference-${index}`}
                value={input.refAr}
                onChange={(event) => handleReferenceChange(event, index)}
                required
              />
              <datalist id="reference-list">
                {references.map((reference, i) => (
                  <option key={i} value={reference} />
                ))}
              </datalist>
            </div>
            <div style={{ marginRight: '10px' }}>
              <label htmlFor={`quantity-${index}`}>Quantité:</label>
              <input
                type="number"
                id={`quantity-${index}`}
                name={`quantity-${index}`}
                value={input.quantité}
                onChange={(event) => handleQuantityChange(event, index)}
                required
              />
            </div>
            <div style={{ marginRight: '10px' }}>
              <label htmlFor={`remise-${index}`}>Remise (%):</label>
              <input
                type="number"
                id={`remise-${index}`}
                name={`remise-${index}`}
                ref={remiseInputRef}
                value={input.remise}
                onChange={(event) => handleRemiseChange(event, index)}
                onKeyPress={handleKeyPress}
                required
              />
            </div>
          </div>
        ))}
        <button type="button" onClick={handleAddInput}>Ajouter une ligne</button>
        <button type="submit" style={{ marginLeft: '10px' }}>{selectedFacture ? 'Mettre à jour la facture' : 'Créer la facture'}</button>
      </form>
    </div>
  );
}

export default CreateFactureVente;
