import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function FactureVente() {
    const [clients, setClients] = useState([]);

    useEffect(() => {
        axios.get('http://localhost:7777/factureVente/')
            .then(res => {
                const formattedClients = res.data.map(client => {
                    const date = new Date(client.date);
                    const options = { timeZone: 'Africa/Tunis' };
                    // Format the date using toLocaleDateString() with the options
                    const formattedDate = date.toLocaleDateString('en-GB', options);
                    return {
                        ...client,
                        date: formattedDate
                    };
                });
                setClients(formattedClients);
            })
            .catch(err => console.log(err));
    }, []);
    const handleDelete = async (id) => {
        try {
            await axios.delete(`http://localhost:7777/factureVente/${id}`);
            window.location.reload();
        } catch (err) {
            console.log(err);
        }
    };

    return (
        <div className='d-flex vh-100 bg-primary justify-content-center align-items-center'>
            <div className='w-110 bg-white rounded p-3'>
                <Link to="/FactureVente/createFac" className='btn btn-success'>Ajouter +</Link>
                <table className='table'>
                    <thead>
                        <tr>
                            <th>Id</th>
                            <th>Nom du client</th>
                            <th>Date</th>
                            <th>Code</th>
                            <th>Ref Article</th>
                            <th>Quantité</th>
                        </tr>
                    </thead>
                    <tbody>
                        {clients.map((client, i) => (
                            <tr key={i}>
                                <td>{client.id}</td>
                                <td>{client.client}</td>
                                <td>{client.date}</td>
                                <td>{client.code}</td>
                                <td>{client.refArticle}</td>
                                <td>{client.quantité}</td>
                                <td>
                                    <Link to={`updateCl/${client.clID}`} className='btn btn-primary'>Changer</Link>
                                    <button className='btn btn-danger ms-2' onClick={e => handleDelete(client.clID)}>Supprimer</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
}

export default FactureVente;
