import axios from 'axios';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';


function CreateArticle() {
    const [name,setName] = useState('')
    const [prix1,setPrix1] = useState('')
    const [prix2,setPrix2] = useState('')
    const [tva,setTva] = useState('')
    const navigate = useNavigate();


function handleSubmit(event) {
    event.preventDefault();
    axios.post('http://localhost:7777/article/createAr/',{
    arName: name,
    arPrice1: prix1,
    arPrice2: prix2,
    arTva: tva
})
    .then(res => {
        console.log(res);
        navigate('/article/');

    }).catch(err => console.log(err))
} 
    
  return (
    <div className='d-flex vh-100 bg-primary justify-content-center align-items-center'>
        <div className='w-50 bg-white rounded p-3'>
            <form onSubmit={handleSubmit}>
                <h2>Ajouter un article</h2>
                <div className='mb-2'>
                    <label htmlFor="">Nom</label>
                    <input type='text' placeholder="entrez le nom d'article" className='form-control'
                    onChange={e => setName(e.target.value)}
                    ></input>
                </div>
                <div className='mb-2'>
                    <label htmlFor="">1er Prix</label>
                    <input type='text' placeholder="entrez le premier prix de cet article" className='form-control'
                    onChange={e => setPrix1(e.target.value)}
                    ></input>
                </div>
                <div className='mb-2'>
                    <label htmlFor="">1er Prix</label>
                    <input type='text' placeholder="entrez le deuxiÃ¨me prix de cet article" className='form-control'
                    onChange={e => setPrix2(e.target.value)}
                    ></input>
                </div>
                <div className='mb-2'>
                    <label htmlFor="">Matricule Fiscale</label>
                    <input type='text' placeholder='entrez le MF du fournisseur' className='form-control'
                    onChange={e => setMF(e.target.value)}
                    ></input>
                </div>
                <div className='mb-2'>
                    <label htmlFor="">Registre Commerciale</label>
                    <input type='text' placeholder='entrez le RC du fournisseur' className='form-control'
                    onChange={e => setRC(e.target.value)}
                    ></input>
                </div>
                <button className='btn btn-success'>Ajouter</button>
            </form>
        </div>
    </div>
  )
}

export default CreateArticle;
