import axios from 'axios';
import React, { useState } from 'react';
import { useNavigate,Link } from 'react-router-dom';


function FactureAchat() {
    const [fss,setFss] = useState('')
    const [date,setDate] = useState('')
    const [code,setCode] = useState('')
    const [refArticle,setRefArticle] = useState('')
    const [quantité,setQuantité] = useState('')

    const navigate = useNavigate();


function handleSubmit(event) {
    event.preventDefault();
    axios.post('http://localhost:7777/factureAchat/createFac/',{
    fss: fss,
    clTel: date,
    clMF: code,
    clRC: refArticle,
    responsable : quantité
})
    .then(res => {
        console.log(res);
        navigate('/client/');

    }).catch(err => console.log(err))
} 
    
  return (
    <div className='d-flex vh-100 bg-primary justify-content-center align-items-center'>
        <div className='w-110 bg-white rounded p-3'>
            <Link to="/client/createCl" className='btn btn-success'>Ajouter +</Link>
            <table className='table'>
                <thead>
                    <tr>
                    <th>Id</th>
                    <th>Nom</th>
                    <th>Adresse</th>
                    <th>Telephone</th>
                    <th>Matricule fiscale</th>
                    <th>Registre Commerciale</th>
                    <th>Nom du resposable</th>
                    <th>Telephone du responsable</th>                    </tr>
                </thead>
                <tbody>
                    {
                        client.map((data,i) => (
                            <tr key={i}>
                                <td>{data.clID}</td>
                                <td>{data.clName}</td>
                                <td>{data.clAdr}</td>
                                <td>{data.clTel}</td>
                                <td>{data.clMF}</td>
                                <td>{data.clRC}</td>
                                <td>{data.responsable}</td>
                                <td>{data.resTel}</td>
                                <td>
                                    <Link to={`updateCl/${data.clID}`} className='btn btn-primary'>Changer</Link>
                                    <button className='btn btn-danger ms-2' onClick={e => handleDelete(data.clID)}>Supprimer</button>
                                </td>
                            </tr>
                        ))
                    }
                </tbody>
            </table>

        </div>
    </div>
  )
}

export default FactureAchat;
