import axios from 'axios';
import React, { useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';


function UpdateClient() {
    const [name,setName] = useState('')
    const [adresse,setAdress] = useState('')
    const [tel,setTel] = useState('')
    const [MF,setMF] = useState('')
    const [RC,setRC] = useState('')
    const {id} = useParams();
    const navigate = useNavigate();


function handleSubmit(event) {
    event.preventDefault();
    axios.put('http://localhost:7777/client/updateCl' + id,{ 
    clName: name,
    clAdr: adresse,
    clTel: tel,
    clMF: MF,
    clRC: RC})
    .then(res => {
        console.log(res);
        navigate('/client/');

    }).catch(err => console.log(err))
} 
    
  return (
    <div className='d-flex vh-100 bg-primary justify-content-center align-items-center'>
        <div className='w-50 bg-white rounded p-3'>
            <form onSubmit={handleSubmit}>
                <h2>Changer les informations du client</h2>
                {/* <div className='mb-2'>
                    <label htmlFor="">Identifiant</label>
                    <input type='text' placeholder="entrez l'identifiant du fournisseur" className='form-control'
                    onChange={e => setName(e.target.value)}
                    ></input>
                </div> */}
                <div className='mb-2'>
                    <label htmlFor="">Nom</label>
                    <input type='text' placeholder='entrez le nom du fournisseur' className='form-control'
                    onChange={e => setName(e.target.value)}
                    ></input>
                </div>
                <div className='mb-2'>
                    <label htmlFor="">Adresse</label>
                    <input type='text' placeholder="entrez l'adresse du fournisseur" className='form-control'
                    onChange={e => setAdress(e.target.value)}
                    ></input>
                </div>
                <div className='mb-2'>
                    <label htmlFor="">Telephone</label>
                    <input type='number' placeholder='entrez le telephone du fournisseur' className='form-control'
                    onChange={e => setTel(e.target.value)}
                    ></input>
                </div>
                <div className='mb-2'>
                    <label htmlFor="">MF</label>
                    <input type='text' placeholder='entrez le MF du fournisseur' className='form-control'
                    onChange={e => setMF(e.target.value)}
                    ></input>
                </div>
                <div className='mb-2'>
                    <label htmlFor="">RC</label>
                    <input type='text' placeholder='entrez le RC du fournisseur' className='form-control'
                    onChange={e => setRC(e.target.value)}
                    ></input>
                </div>
                <button className='btn btn-success'>Changer</button>
            </form>
        </div>
    </div>
  )
}


export default UpdateClient;
