import React, { useState, useEffect } from 'react';
import CreateFactureVente from './CreateFactureVente';
import axios from 'axios';

function FactureVente() {
    const [factures, setFactures] = useState([]);
    const [selectedFacture, setSelectedFacture] = useState(null);

    useEffect(() => {
        axios.get('http://localhost:7777/factureVente')
            .then(res => {
                setFactures(res.data);
            })
            .catch(err => console.error('Error fetching factures:', err));
    }, []);

    const handleDelete = (id) => {
        if (window.confirm('Are you sure you want to delete this facture?')) {
            axios.delete(`http://localhost:7777/factureVente/${id}`)
                .then(() => {
                    setFactures(factures.filter(facture => facture._id !== id));
                })
                .catch(err => console.error('Error deleting facture:', err));
        }
    };

    const handleFactureSubmit = (newFacture) => {
        if (selectedFacture) {
            axios.put(`http://localhost:7777/factureVente/${selectedFacture._id}`, newFacture)
                .then((res) => {
                    setFactures(factures.map(facture => facture._id === selectedFacture._id ? res.data : facture));
                    setSelectedFacture(null);
                })
                .catch(err => console.error('Error updating facture:', err));
        } else {
            axios.post('http://localhost:7777/factureVente', newFacture)
                .then((res) => {
                    setFactures([...factures, res.data]);
                })
                .catch(err => console.error('Error adding facture:', err));
        }
    };

    const handleFactureSelect = (facture) => {
        setSelectedFacture(facture);
    };

    return (
        <div>
            <h1>Factures de Vente</h1>
            <CreateFactureVente onFactureSubmit={handleFactureSubmit} selectedFacture={selectedFacture} />
            <table>
                <thead>
                    <tr>
                        <th>Nom du Client</th>
                        <th>Code Client</th>
                        <th>Date</th>
                        <th>Droit de Timbre</th>
                        <th>Articles</th>
                        <th>Total</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {factures.map(facture => (
                        <tr key={facture._id}>
                            <td>{facture.clName}</td>
                            <td>{facture.client_id}</td>
                            <td>{facture.date}</td>
                            <td>{facture.DT}</td>
                            <td>
                                {facture.inputs.map((input, index) => (
                                    <div key={index}>
                                        Référence: {input.refAr}, Quantité: {input.quantité}, Prix: {input.price}, Remise: {input.remise}%
                                    </div>
                                ))}
                            </td>
                            <td>{facture.totalPrice}</td>
                            <td>
                                <button onClick={() => handleDelete(facture._id)}>Supprimer</button>
                                <button onClick={() => handleFactureSelect(facture)}>Modifier</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default FactureVente;
