import axios from 'axios';
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';


function CreateFactureAchat() {
    const [fss,setFss] = useState('')
    const [date,setDate] = useState('')
    const [code,setCode] = useState('')
    const [refArticle,setRefArticle] = useState('')
    const [quantité,setQuantité] = useState('')

    const navigate = useNavigate();

function handleSubmit(event) {
    event.preventDefault();
    axios.post('http://localhost:7777/factureAchat/createFac/',{
    fss: fss,
    date: date,
    code: code,
    refArticle: refArticle,
    quantité : quantité
})
    .then(res => {
        console.log(res);
        window.location.reload();

    }).catch(err => console.log(err))
} 
    
  return (
    <div className='container mt-5'>
        <h2 className="mb-4">Ajouter une facture d'achat</h2>
        <form onSubmit={handleSubmit}>
        <div className="row mb-3">
            <div className='col'>
                <label htmlFor="">Nom du fournisseur</label>
                <input type='text' placeholder='entrez le nom du fournisseur' className='form-control'
                onChange={e => setFss(e.target.value)}
                ></input>
            </div>
            <div className='col'>
                <label htmlFor="">date</label>
                <input type="date" 
                className='form-control'
                onChange={e => setDate(e.target.value)}
                ></input>
            </div>
        </div>
        <div className="row mb-3">    
            <div className='col'>
                <label htmlFor="">Code de la facture</label>
                <input type='text' placeholder='entrez le code de la facture' className='form-control'
                onChange={e => setCode(e.target.value)}
                ></input>
            </div>
            <div className='col'>
                <label htmlFor="">Réference d'article</label>
                <input type='text' placeholder="entrez la Réference d'article" className='form-control'
                onChange={e => setRefArticle(e.target.value)}
                ></input>
            </div>
        </div>
            <div className='mb-2'>
                <label htmlFor="">Quantité</label>
                <input type='number' placeholder='entrez la quantité désirée' className='form-control'
                onChange={e => setQuantité(e.target.value)}
                ></input>
            </div>
            <button className='btn btn-success'>Ajouter</button>
        </form>

</div>
  )
}

export default CreateFactureAchat;
