import React, { useState, useEffect } from 'react';
import axios from 'axios';

function CreateFactureVente({ onAddFVente, onDelete, selectedFacture }) {
    const [client, setClient] = useState('');
    const [date, setDate] = useState('');
    const [code, setCode] = useState('');
    const [inputs, setInputs] = useState([{ refAr: '', quantité: '', price: 0, remise: 0 }]);
    const [references, setReferences] = useState([]);
    const [telephone, setTelephone] = useState('');

    useEffect(() => {
        axios.get('http://localhost:7777/article')
            .then(res => {
                setReferences(res.data.map(article => article.ref));
            })
            .catch(err => console.log(err));
    }, []);

    const fetchPriceByReference = (reference, index) => {
        axios.get(`http://localhost:7777/factureVente/${reference}`) 
            .then(res => {
                const price = res.data.price;
                const newInputs = [...inputs];
                newInputs[index].price = price;
                setInputs(newInputs);
            })
            .catch(err => console.log(err));
    };

    const handleReferenceChange = (event, index) => {
        const { value } = event.target;
        const newInputs = [...inputs];
        newInputs[index].refAr = value;
        setInputs(newInputs);
        fetchPriceByReference(value, index); 
    };

    const handleQuantityChange = (event, index) => {
        const { value } = event.target;
        const newInputs = [...inputs];

        axios.get(`http://localhost:7777/article/${newInputs[index].refAr}/quantity`)
            .then(res => {
                const availableQuantity = res.data.quantity;

                const remainingQuantity = availableQuantity - parseInt(value);

                if (remainingQuantity < 0) {
                    alert(`La quantité souhaitée n'est pas suffisante, vous n'avez que ${availableQuantity} pièces de cet article`);
                    newInputs[index].quantité = availableQuantity;
                } else {
                    newInputs[index].quantité = value;
                }

                // Update price with remise calculation
                const remisePrice = newInputs[index].price * (1 - (newInputs[index].remise / 100));
                newInputs[index].price = parseFloat(remisePrice.toFixed(2)); // Round to 2 decimal places
                setInputs(newInputs);
            })
            .catch(err => console.error(err));

        // Recalculate total
        const newTotal = newInputs.reduce((acc, curr) => acc + (curr.price * curr.quantité), 0);
        console.log("New Total:", newTotal);
    };

    const handleAddInput = () => {
        setInputs([...inputs, { refAr: '', price: 0, quantité: '', remise: 0 }]);
    };

    const calculateTotal = () => {
        return inputs.reduce((acc, curr) => acc + (curr.price * curr.quantité) * (1), 0);
    };

    const handleSubmit = (event) => {
        event.preventDefault();

        const invalidReference = inputs.some(input => !references.includes(input.refAr));
        if (invalidReference) {
            alert("Sélectionner une référence valide");
            return;
        }

        const refArArray = [];
        const quantiteArray = [];
        const remiseArray = [];
        inputs.forEach(input => {
            refArArray.push(input.refAr);
            quantiteArray.push(input.quantité);
            remiseArray.push(input.remise);
        });

        const totalPrice = calculateTotal();
        axios.post('http://localhost:7777/factureVente/createFac/', {
            client,
            date,
            code,
            refAr: refArArray,
            quantité: quantiteArray,
            remise: remiseArray,
            totalPrice
        })
            .then(res => {
                console.log(res);
                window.location.reload();
            })
            .catch(err => console.log(err))
    };

    return (
        <div style={{ margin: '20px', justifyContent: 'center' }}>
            <div style={{display:'flex', flex: '1'}}>
                <div>
                    <div style={{ marginBottom: '20px' }}>
                        <label style={{ width: '120px' }} htmlFor="client">Nom du client</label>
                        <input
                            style={{ width: '200px' }}
                            type="text"
                            id="client"
                            value={client}
                            onChange={(e) => setClient(e.target.value)}
                        />
                    </div>
                    <div style={{ marginBottom: '20px' }}>
                        <label style={{ width: '120px' }} htmlFor="date">Date</label>
                        <input
                            style={{ width: '200px' }}
                            type="date"
                            id="date"
                            value={date}
                            onChange={(e) => setDate(e.target.value)}
                        />
                    </div>
                    <div style={{ marginBottom: '20px' }}>
                        <label style={{ width: '120px' }} htmlFor="code">Code facture</label>
                        <input
                            style={{ width: '200px' }}
                            type="text"
                            id="code"
                            value={code}
                            onChange={(e) => setCode(e.target.value)}
                        />
                    </div>
                    <div style={{ marginBottom: '20px' }}>
                        <label style={{ width: '120px' }} htmlFor="telephone">Téléphone</label>
                        <input
                            style={{ width: '200px' }}
                            type="text"
                            id="telephone"
                            value={telephone}
                            onChange={(e) => setTelephone(e.target.value)}
                        />
                    </div>
                </div>
                <div>
                    <div>
                        {inputs.map((input, index) => (
                            <div key={index} style={{ display: 'flex', marginBottom: '10px' }}>
                                <div style={{ flex: '1', marginRight: '10px' }}>
                                    <label>Référence d'article</label>
                                    <input
                                        list="references"
                                        style={{ width: '100%' }}
                                        value={input.refAr}
                                        onChange={(e) => handleReferenceChange(e, index)}
                                    />
                                    <datalist id="references">
                                        {references.map((reference, idx) => (
                                            <option key={idx} value={reference} />
                                        ))}
                                    </datalist>
                                </div>
                                <div style={{ flex: '1', marginRight: '10px' }}>
                                    <label>Prix vente TTC</label>
                                    <input
                                        type="number"
                                        style={{ width: '100%' }}
                                        value={input.price}
                                        readOnly
                                    />
                                </div>
                                <div style={{ flex: '1', marginRight: '10px' }}>
                                    <label>Quantité</label>
                                    <input
                                        type="number"
                                        style={{ width: '100%' }}
                                        value={input.quantité}
                                        onChange={(e) =>                                    handleQuantityChange(e, index)}
                                        />
                                    </div>
                                    <div style={{ flex: '1' }}>
                                        <label>Remise</label>
                                        <input
                                            type="number"
                                            style={{ width: '100%' }}
                                            value={input.remise}
                                            onChange={(e) => {
                                                const newInputs = [...inputs];
                                                newInputs[index].remise = e.target.value;
                                                setInputs(newInputs);
                                                handleQuantityChange(e, index); // Trigger recalculation when remise changes
                                            }}
                                        />
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>
                    <div>
                        <div style={{ marginBottom: '20px', textAlign: 'center' }}>
                            <button
                                style={{ width: '200px', marginBottom: '10px' }}
                                type="button"
                                onClick={handleAddInput}
                            >
                                Ajouter
                            </button>
                            <button
                                style={{ width: '200px', marginBottom: '10px' }}
                                type="submit"
                                onClick={handleSubmit}
                            >
                                Enregistrer
                            </button>
                        </div>
                    </div>
                </div>
                <div style={{ marginBottom: '20px' }}>
                    <label>Total</label>
                    <input
                        type="number"
                        style={{ width: '100%' }}
                        value={calculateTotal()}
                        readOnly
                    />
                </div>
            </div>
        );
    }
    
    export default CreateFactureVente;
