import React, { useState, useEffect } from "react";
import axios from "axios";

function CreateArticle({ onAddArticle, onDelete, selectedArticle }) {
  const [name, setName] = useState("");
  const [ref, setRef] = useState("");
  const [achatbrut, setAchatbrut] = useState("");
  const [remise1, setRemise1] = useState("");
  const [remise2, setRemise2] = useState("0");
  const [achatttc, setAchatttc] = useState("");
  const [achatc, setAchatc] = useState("");
  const [mbenif, setMbenif] = useState("");
  const [venteht, setVenteht] = useState("");
  const [ventettc, setVentettc] = useState("");
  // const [autofillar1, setAutofillar1] = useState("");
  // const [autofillar2, setAutofillar2] = useState("0");
  // const [selectedTva, setSelectedTva] = useState("0");
  // const [quantite, setQuantite] = useState("");

  useEffect(() => {
    if (selectedArticle) {
      setName(selectedArticle.arName || "");
      setRef(selectedArticle.ref || "");
      setAchatbrut((selectedArticle.prachat || "").toFixed(3));
      setRemise1((selectedArticle.remise1 || "").toFixed(3));
      setRemise2((selectedArticle.remise2 || "0").toFixed(3));
      setAchatttc((selectedArticle.achatttc || "").toFixed(3));
      setAchatc((selectedArticle.achatc || "").toFixed(3));
      setMbenif((selectedArticle.margeben || "").toFixed(3));
      setVenteht((selectedArticle.prventeht || "").toFixed(3));
      setVentettc((selectedArticle.prventettc || "").toFixed(3));
      setAutofillar1((selectedArticle.prar1 || "").toFixed(3));
      setAutofillar2((selectedArticle.prar2 || "0").toFixed(3));
      setSelectedTva((selectedArticle.tva?.toString() || "0").toFixed(3));
      setQuantite((selectedArticle.quantite || "").toFixed(3));
    }
  }, [selectedArticle]);

  const handleSubmit = (event) => {
    event.preventDefault();
    if (selectedTva !== null && selectedTva !== undefined) {
      axios
        .post("http://localhost:7777/article/createAr/", {
          arName: name,
          ref: ref,
          prachat: parseFloat(achatbrut).toFixed(3),
          remise1: parseFloat(remise1).toFixed(3),
          prar1: parseFloat(autofillar1).toFixed(3),
          remise2: parseFloat(remise2).toFixed(3),
          prar2: parseFloat(autofillar2).toFixed(3),
          tva: parseInt(selectedTva),
          achatttc: parseFloat(achatttc).toFixed(3),
          achatc: parseFloat(achatc).toFixed(3),
          margeben: parseFloat(mbenif).toFixed(3),
          prventeht: parseFloat(venteht).toFixed(3),
          prventettc: parseFloat(ventettc).toFixed(3),
          quantite: parseFloat(quantite).toFixed(3)
        })
        .then((res) => {
          window.location.reload();
        })
        .catch((err) => console.log(err));
    } else {
      console.log("Invalid value of tva");
    }
  };

  const handleUpdate = (event) => {
    event.preventDefault();
    if (selectedArticle) {
      axios
        .put(`http://localhost:7777/article/updateAr/${selectedArticle.arID}`, {
          arName: name,
          ref: ref,
          prachat: parseFloat(achatbrut).toFixed(3),
          remise1: parseFloat(remise1).toFixed(3),
          prar1: parseFloat(autofillar1).toFixed(3),
          remise2: parseFloat(remise2).toFixed(3),
          prar2: parseFloat(autofillar2).toFixed(3),
          tva: parseInt(selectedTva),
          achatttc: parseFloat(achatttc).toFixed(3),
          achatc: parseFloat(achatc).toFixed(3),
          margeben: parseFloat(mbenif).toFixed(3),
          prventeht: parseFloat(venteht).toFixed(3),
          prventettc: parseFloat(ventettc).toFixed(3),
          quantite: parseFloat(quantite).toFixed(3)
        })
        .then((res) => {
          console.log("Article updated successfully");
          window.location.reload();
        })
        .catch((err) => console.log(err));
    } else {
      console.log("No article selected for update");
    }
  };

  // useEffect(() => {
  //   if (achatbrut && remise1) {
  //     const prixAR1 = parseFloat(achatbrut) * (1 - parseFloat(remise1) / 100);
  //     setAutofillar1(prixAR1.toFixed(3));
  //   } else {
  //     setAutofillar1("");
  //   }
  // }, [achatbrut, remise1]);
  useEffect(() => {
    if (achatbrut && remise1) {
      const prixAR1 = parseFloat(achatbrut) * (1 - parseFloat(remise1) / 100);
      setAutofillar1(prixAR1.toFixed(3));
    } else {
      setAutofillar1("");
    }
  }, [achatbrut, remise1]);

  useEffect(() => {
    if (autofillar1 && remise2) {
      const prixAR2 = parseFloat(autofillar1) * (1 - parseFloat(remise2) / 100);
      setAutofillar2(prixAR2.toFixed(3));
    } else {
      setAutofillar2("");
    }
  }, [autofillar1, remise2]);

  useEffect(() => {
    if (autofillar1 && remise2) {
      setAchatttc(autofillar2.toFixed(3));
    } else {
      setAchatttc(autofillar1.toFixed(3));
    }
  }, [autofillar1, autofillar2, remise2]);

  useEffect(() => {
    if (achatttc && mbenif) {
      const sum = (parseFloat(achatttc) / 100 * parseFloat(mbenif)) + parseFloat(achatttc);
      setVenteht(sum.toFixed(3));
    } else {
      setVenteht("");
    }
  }, [achatttc, mbenif]);

  useEffect(() => {
    if (venteht && selectedTva) {
      const sum = parseFloat(venteht) + (parseFloat(venteht) * parseFloat(selectedTva) / 100);
      setVentettc(sum.toFixed(3));
    } else {
      setVentettc("");
    }
  }, [venteht, selectedTva]);

  useEffect(() => {
    if (achatttc && selectedTva) {
      const sum = parseFloat(achatttc) + (parseFloat(achatttc) * parseFloat(selectedTva) / 100);
      setAchatc(sum.toFixed(3));
    } else {
      setAchatc("");
    }
  }, [achatttc, selectedTva]);

  const handleClearInputs = () => {
    setName("");
    setRef("");
    setAchatbrut("");
    setRemise1("");
    setRemise2("0");
    setAchatttc("");
    setAchatc("");
    setMbenif("");
    setVenteht("");
    setVentettc("");
    setAutofillar1("");
    setAutofillar2("0");
    setSelectedTva("0");
    setQuantite("");
  };

  return (
    <div style={{ margin: "20px", display: "flex", justifyContent: "flex-end" }}>
      <div style={{ flex: "0.7", marginRight: "10px" }}>
        <div style={{ marginBottom: "10px" }}>
          <label style={{ width: "120px" }} htmlFor="ref">Référence</label>
          <input
            style={{ width: "200px" }}
            type="text"
            id="ref"
            value={ref}
            onChange={(e) => setRef(e.target.value)}
          />
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label style={{ width: "120px" }} htmlFor="name">Nom article</label>
          <input
            style={{ width: "200px" }}
            type="text"
            id="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label style={{ width: "120px" }} htmlFor="achatbrut">Prix Achat Brut</label>
          <input
            style={{ width: "200px" }}
            type="number"
            step="0.001"
            id="achatbrut"
            value={achatbrut}
            onChange={(e) => setAchatbrut(e.target.value)}
          />
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label style={{ width: "120px" }} htmlFor="remise1">Remise 1 (%)</label>
          <input
            style={{ width: "200px" }}
            type="number"
            step="0.001"
            id="remise1"
            value={remise1}
            onChange={(e) => setRemise1(e.target.value)}
          />
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label style={{ width: "120px" }} htmlFor="remise2">Remise 2 (%)</label>
          <input
            style={{ width: "200px" }}
            type="number"
            step="0.001"
            id="remise2"
            value={remise2}
            onChange={(e) => setRemise2(e.target.value)}
          />
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label style={{ width: "120px" }} htmlFor="quantite">Quantité</label>
          <input
            style={{ width: "200px" }}
            type="number"
            step="0.001"
            id="quantite"
            value={quantite}
            onChange={(e) => setQuantite(e.target.value)}
          />
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label style={{ width: "120px" }} htmlFor="mbenif">Marge Benifice</label>
          <input
            style={{ width: "200px" }}
            type="number"
            step="0.001"
            id="mbenif"
            value={mbenif}
            onChange={(e) => setMbenif(e.target.value)}
          />
        </div>
      </div>
      <div style={{ flex: "0.3" }}>
        <div style={{ marginBottom: "10px" }}>
          <label style={{ width: "120px" }} htmlFor="selectedTva">TVA</label>
          <select
            style={{ width: "200px" }}
            id="selectedTva"
            value={selectedTva}
            onChange={(e) => setSelectedTva(e.target.value)}
          >
            <option value="0">0</option>
            <option value="1">1</option>
            <option value="7">7</option>
            <option value="19">19</option>
          </select>
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label style={{ width: "120px" }} htmlFor="achatttc">Prix Achat TTC</label>
          <input
            style={{ width: "200px" }}
            type="number"
            step="0.001"
            id="achatttc"
            value={achatttc}
            onChange={(e) => setAchatttc(e.target.value)}
          />
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label style={{ width: "120px" }} htmlFor="achatc">Achat avec TVA</label>
          <input
            style={{ width: "200px" }}
            type="number"
            step="0.001"
            id="achatc"
            value={achatc}
            onChange={(e) => setAchatc(e.target.value)}
          />
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label style={{ width: "120px" }} htmlFor="venteht">Prix Vente HT</label>
          <input
            style={{ width: "200px" }}
            type="number"
            step="0.001"
            id="venteht"
            value={venteht}
            onChange={(e) => setVenteht(e.target.value)}
          />
        </div>
        <div style={{ marginBottom: "10px" }}>
          <label style={{ width: "120px" }} htmlFor="ventettc">Prix Vente TTC</label>
          <input
            style={{ width: "200px" }}
            type="number"
            step="0.001"
            id="ventettc"
            value={ventettc}
            onChange={(e) => setVentettc(e.target.value)}
          />
        </div>
        <div>
          <button onClick={handleSubmit}>Add Article</button>
          <button onClick={handleUpdate}>Update Article</button>
          <button onClick={handleClearInputs}>Clear</button>
        </div>
      </div>
    </div>
  );
}

export default CreateArticle;
